// src/pages/DownloadsPage.tsx
import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { Link } from 'react-router-dom';
import { supabase } from '../supabaseClient';
import { useUser } from '../context/UserContext'; // Import the useUser hook

type File = {
  id: string;
  filename: string;
  created_at: string;
};

// --- Styled Components ---
const PageHeader = styled.h2` font-size: 24px; font-weight: 600; margin-bottom: 24px; `;
const DownloadsTable = styled.table` width: 100%; border-collapse: collapse; background-color: white; border: 1px solid #E0E0E0; border-radius: 8px; overflow: hidden; th, td { padding: 16px 24px; text-align: left; border-bottom: 1px solid #f0f0f0; } th { background-color: #fafafa; font-weight: 600; } tr:last-child td { border-bottom: none; } `;
const ButtonContainer = styled.div` display: flex; gap: 12px; `;
const PrimaryButton = styled(Link)` display: inline-block; background-color: #2c2c54; color: white; padding: 10px 20px; border-radius: 8px; text-decoration: none; font-weight: 500; `;
const SecondaryButton = styled(Link)` display: inline-block; background-color: white; color: #2c2c54; border: 1px solid #2c2c54; padding: 10px 20px; border-radius: 8px; text-decoration: none; font-weight: 500; `;
const MessageContainer = styled.div` padding: 40px; text-align: center; color: #757575; background-color: white; border-radius: 12px; border: 1px solid #EAEAEA; `;

const DownloadsPage = () => {
  const { user } = useUser(); // Get the logged-in user from our context
  const [files, setFiles] = useState<File[]>([]);
  const [isLoading, setLoading] = useState(true);

  useEffect(() => {
    // Only try to fetch files if we have a logged-in user
    if (user && user.simulation_user_id) {
      setLoading(true);

      async function getPurchasedFiles() {
        try {
          // 1. Get the list of file IDs the user purchased from the user_purchases table
          const { data: purchases, error: purchaseError } = await supabase
            .from('user_purchases')
            .select('file_id')
            .eq('user_id', user.simulation_user_id);

          if (purchaseError) throw purchaseError;
          if (!purchases || purchases.length === 0) {
            setFiles([]);
            return;
          }

          const fileIds = purchases.map(p => p.file_id);

          // 2. Use those IDs to get the actual file details from the files table
          const { data: fileData, error: fileError } = await supabase
            .from('files')
            .select('id, filename, created_at')
            .in('id', fileIds);

          if (fileError) throw fileError;

          setFiles(fileData || []);

        } catch (error) {
          console.error('Error fetching purchased files:', error);
          setFiles([]);
        } finally {
          setLoading(false);
        }
      }

      getPurchasedFiles();
    } else {
      setLoading(false); // If there's no user, stop loading
    }
  }, [user]); // This effect re-runs whenever the user object changes

  if (isLoading) return <MessageContainer>Loading your downloads...</MessageContainer>;
  if (!user) return <MessageContainer>Please log in to view your downloads.</MessageContainer>;
  if (files.length === 0) return <MessageContainer>You have no available downloads.</MessageContainer>;

  return (
    <div>
      <PageHeader>Downloads</PageHeader>
      <DownloadsTable>
        <thead>
          <tr>
            <th>Product</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {files.map((file) => (
            <tr key={file.id}>
              <td>{file.filename}</td>
              <td>
                <ButtonContainer>
                  <PrimaryButton to="#">Download File</PrimaryButton>
                  <SecondaryButton to={`/practice/${file.id}`}>Practice</SecondaryButton>
                </ButtonContainer>
              </td>
            </tr>
          ))}
        </tbody>
      </DownloadsTable>
    </div>
  );
};

export default DownloadsPage;
