// src/pages/DashboardPage.tsx
import React from 'react';
import styled from 'styled-components';

const WelcomeHeader = styled.h2`
  font-size: 24px;
  font-weight: 600;
  margin-bottom: 24px;
`;

const WidgetsContainer = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(300px, 1fr));
  gap: 24px;
`;

const Widget = styled.div`
  background-color: ${({ theme }) => theme.colors.surface};
  border: 1px solid ${({ theme }) => theme.colors.lightGrey};
  border-radius: 8px;
  padding: 24px;

  h3 {
    font-size: 18px;
    margin-bottom: 16px;
  }

  p {
    color: ${({ theme }) => theme.colors.textSecondary};
  }
`;

const DashboardPage = () => {
  // We'll replace this with real user data later
  const userName = "Ahmed R."; 

  return (
    <div>
      <WelcomeHeader>Hello, {userName} ðŸ‘‹</WelcomeHeader>
      <WidgetsContainer>
        <Widget>
          <h3>Orders</h3>
          <p>You have no recent orders.</p>
        </Widget>
        <Widget>
          <h3>Practice</h3>
          <p>Start a new simulation.</p>
        </Widget>
        <Widget>
          <h3>Account Details</h3>
          <p>Manage your password and details.</p>
        </Widget>
      </WidgetsContainer>
    </div>
  );
};

export default DashboardPage;
