// src/components/common/Sidebar.tsx
import React from 'react';
import styled from 'styled-components';
import { NavLink } from 'react-router-dom';

// ********************************************************************
// THE FIX: We are using a different, more stable icon set for now.
import { FaClipboardList, FaChartBar, FaTrophy, FaTasks } from 'react-icons/fa';
// ********************************************************************

// Import your logo
import logo from '../../assets/images/logo.png';

const SidebarContainer = styled.div`
  width: 260px;
  background-color: ${({ theme }) => theme.colors.surface};
  height: 100vh;
  display: flex;
  flex-direction: column;
  border-right: 1px solid ${({ theme }) => theme.colors.lightGrey};
  position: fixed;
  left: 0;
  top: 0;
`;

const LogoContainer = styled.div`
  padding: 24px;
  display: flex;
  justify-content: center;
  align-items: center;
  margin-bottom: 20px;

  img {
    width: 150px;
  }
`;

const NavList = styled.nav`
  display: flex;
  flex-direction: column;
`;

const StyledNavLink = styled(NavLink)`
  display: flex;
  align-items: center;
  padding: 16px 24px;
  color: ${({ theme }) => theme.colors.textSecondary};
  text-decoration: none;
  font-weight: 500;
  transition: all 0.2s ease-in-out;

  svg {
    margin-right: 16px;
    font-size: 20px; /* Adjusted size slightly for these icons */
    width: 20px;     /* Ensure consistent width */
  }

  &.active, &:hover {
    background-color: #E6F0FF; 
    color: ${({ theme }) => theme.colors.primary};

    svg {
      color: ${({ theme }) => theme.colors.primary};
    }
  }
`;

const Sidebar = () => {
  return (
    <SidebarContainer>
      <LogoContainer>
        <img src={logo} alt="CertEmpire Logo" />
      </LogoContainer>
      <NavList>
        <StyledNavLink to="/" end>
          {/* Icons are changed here */}
          <FaClipboardList />
          Simulation
        </StyledNavLink>
        <StyledNavLink to="/reports">
          <FaChartBar />
          Report History
        </StyledNavLink>
        <StyledNavLink to="/rewards">
          <FaTrophy />
          My Rewards
        </StyledNavLink>
        <StyledNavLink to="/tasks">
          <FaTasks />
          My Tasks
        </StyledNavLink>
      </NavList>
    </SidebarContainer>
  );
};

export default Sidebar;
