// src/components/common/Sidebar.tsx
import React from 'react';
import styled from 'styled-components';
import { NavLink } from 'react-router-dom';
import logo from '../../assets/images/logo.png'; // Make sure logo.png is in src/assets/images/

const SidebarContainer = styled.div`
  width: 260px;
  background-color: ${({ theme }) => theme.colors.surface};
  height: 100vh;
  display: flex;
  flex-direction: column;
  border-right: 1px solid ${({ theme }) => theme.colors.lightGrey};
  position: fixed;
  left: 0;
  top: 0;
`;

const LogoContainer = styled.div`
  padding: 24px;
  display: flex;
  justify-content: center;
  align-items: center;
  margin-bottom: 20px;

  img {
    width: 150px;
  }
`;

const NavList = styled.nav`
  display: flex;
  flex-direction: column;
`;

// Updated NavLink without icon-specific spacing
const StyledNavLink = styled(NavLink)`
  padding: 16px 24px;
  color: ${({ theme }) => theme.colors.textSecondary};
  text-decoration: none;
  font-weight: 500;
  transition: all 0.2s ease-in-out;

  &.active, &:hover {
    background-color: #E6F0FF; 
    color: ${({ theme }) => theme.colors.primary};
  }
`;

// Updated LogoutButton without icon-specific spacing
const LogoutButton = styled.button`
  width: 100%;
  padding: 16px 24px;
  color: ${({ theme }) => theme.colors.textSecondary};
  font-size: 16px;
  font-weight: 500;
  font-family: inherit;
  background: none;
  border: none;
  cursor: pointer;
  text-align: left;

  &:hover {
    background-color: #E6F0FF; 
    color: ${({ theme }) => theme.colors.primary};
  }
`;

const Sidebar = () => {
  return (
    <SidebarContainer>
      <LogoContainer>
        <img src={logo} alt="CertEmpire Logo" />
      </LogoContainer>
      <NavList>
        {/* Links with NO ICONS */}
        <StyledNavLink to="/" end>Dashboard</StyledNavLink>
        <StyledNavLink to="/orders">Orders</StyledNavLink>
        <StyledNavLink to="/downloads">Downloads</StyledNavLink>
        <StyledNavLink to="/practice">Practice</StyledNavLink>
        <StyledNavLink to="/addresses">Addresses</StyledNavLink>
        <StyledNavLink to="/account-details">Account Details</StyledNavLink>
        <LogoutButton onClick={() => alert('Logout clicked!')}>Log out</LogoutButton>
      </NavList>
    </SidebarContainer>
  );
};

export default Sidebar;
