// src/components/common/Sidebar.tsx
import React from 'react';
import styled from 'styled-components';
import { NavLink } from 'react-router-dom';
import { useUser } from '../../context/UserContext';

// The props interface for the component (for mobile state)
interface SidebarProps {
  isOpen: boolean;
}

// This container now matches the floating panel design from your Flutter app
const SidebarContainer = styled.aside<{ isOpen: boolean; }>`
  width: 260px;
  margin: 24px;
  background-color: ${({ theme }) => theme.colors.surface};
  border-radius: ${({ theme }) => theme.borderRadius.large};
  box-shadow: ${({ theme }) => theme.shadows.card};
  border: 1px solid ${({ theme }) => theme.colors.lightGrey};
  padding: 8px;

  // This makes it a self-contained scrolling panel on desktop
  align-self: flex-start;
  position: sticky;
  top: 24px;

  // Responsive logic for mobile/tablet view
  @media (max-width: 768px) {
    position: fixed;
    left: 0;
    top: 0;
    height: 100%;
    width: 280px; // A bit wider for touch targets
    margin: 0;
    border-radius: 0;
    transform: ${({ isOpen }) => isOpen ? 'translateX(0)' : 'translateX(-100%)'};
    transition: transform 0.3s ease-in-out;
    z-index: 1000;
    box-shadow: none;
    padding: 0;
    background-color: ${({ theme }) => theme.colors.background};
  }
`;

const NavList = styled.nav`
  display: flex;
  flex-direction: column;
  gap: 4px; /* Space between nav items */

  @media (max-width: 768px) {
    gap: 0;
    padding-top: 80px; /* Space below the header */
  }
`;

// Base styles for both NavLink and Button to ensure they look the same
const navItemStyles = `
  color: ${({ theme }) => theme.colors.textSecondary};
  text-decoration: none;
  font-weight: 500;
  font-size: 15px;
  padding: 12px 20px;
  border-radius: ${({ theme }) => theme.borderRadius.medium};
  transition: all 0.2s ease-in-out;
  border: 1px solid transparent;

  &:hover {
    background-color: ${({ theme }) => theme.colors.background};
    color: ${({ theme }) => theme.colors.textPrimary};
  }
`;

const StyledNavLink = styled(NavLink)`
  ${navItemStyles}

  /* --- Styles for the ACTIVE link --- */
  &.active {
    background-color: ${({ theme }) => theme.colors.button};
    color: ${({ theme }) => theme.colors.primary};
    font-weight: 600;
  }

  @media (max-width: 768px) {
    border-radius: 0;
    border-bottom: 1px solid ${({ theme }) => theme.colors.lightGrey};
  }
`;

const LogoutButton = styled.button`
  ${navItemStyles}
  background: none;
  cursor: pointer;
  text-align: left;
  width: 100%;
  font-family: inherit;

  @media (max-width: 768px) {
    border-radius: 0;
  }
`;

const Sidebar: React.FC<SidebarProps> = ({ isOpen }) => {
  const { logout } = useUser();

  return (
    <SidebarContainer isOpen={isOpen}>
      <NavList>
        <StyledNavLink to="/" end>Dashboard</StyledNavLink>
        <StyledNavLink to="/orders">Orders</StyledNavLink>
        <StyledNavLink to="/downloads">Downloads</StyledNavLink>
        <StyledNavLink to="/addresses">Addresses</StyledNavLink>
        <StyledNavLink to="/account-details">Account Details</StyledNavLink>
        <LogoutButton onClick={logout}>Log out</LogoutButton>
      </NavList>
    </SidebarContainer>
  );
};

export default Sidebar;
