// --- Type Definitions (This is the main fix) ---
type Question = {
  type: 'question';
  id: string;
  qNumber: number;
  text: string;
  description: string;
  options: string[];
  correctAnswerIndices: number[];
  explanation: string;
};

type CaseStudy = {
  type: 'case_study';
  title: string;
  description: string;
  questions: Question[];
};

type Topic = {
  type: 'topic';
  title: string;
  items: (Topic | CaseStudy)[];
};

type SimulationItem = Question | Topic | CaseStudy;


// --- Helper function to get a flat list of questions (Now with proper types) ---
const getAllQuestions = (items: SimulationItem[]): Question[] => {
  let questions: Question[] = [];
  items.forEach((item: SimulationItem) => {
    if (item.type === 'question') {
      questions.push(item);
    } else if (item.type === 'topic' && item.items) {
      questions = [...questions, ...getAllQuestions(item.items)];
    } else if (item.type === 'case_study' && item.questions) {
      questions = [...questions, ...item.questions];
    }
  });
  return questions;
};

