// src/pages/DownloadsPage.tsx
import React from 'react';
import styled from 'styled-components';

// --- Sample Data ---
const sampleDownloads = [
  { id: 'd1', product: 'CISSP - Certified Information Systems Security Professional', expires: 'Never', link: '#' },
  { id: 'd2', product: 'CompTIA Security+ (SY0-601)', expires: 'Never', link: '#' },
  // The typo is fixed on this line. 'd3' now has quotes around it.
  { id: 'd3', product: 'Certified Ethical Hacker (CEH) v11', expires: 'October 25, 2025', link: '#' },
];

// --- Styled Components ---
const PageHeader = styled.h2`
  font-size: 24px;
  font-weight: 600;
  margin-bottom: 24px;
`;

const DownloadsTable = styled.table`
  width: 100%;
  border-collapse: collapse;
  background-color: ${({ theme }) => theme.colors.surface};
  border: 1px solid ${({ theme }) => theme.colors.lightGrey};
  border-radius: 8px;
  overflow: hidden;

  th, td {
    padding: 16px 24px;
    text-align: left;
    border-bottom: 1px solid ${({ theme }) => theme.colors.lightGrey};
  }

  th {
    background-color: #fafafa;
    font-weight: 600;
  }

  tr:last-child td {
    border-bottom: none;
  }
`;

const DownloadButton = styled.a`
  background-color: ${({ theme }) => theme.colors.primary};
  color: white;
  padding: 8px 16px;
  border-radius: 6px;
  text-decoration: none;
  font-weight: 500;
  transition: background-color 0.2s;

  &:hover {
    background-color: ${({ theme }) => theme.colors.primaryDark};
  }
`;

const DownloadsPage = () => {
  return (
    <div>
      <PageHeader>Downloads</PageHeader>
      <DownloadsTable>
        <thead>
          <tr>
            <th>Product</th>
            <th>Expires</th>
            <th>Download</th>
          </tr>
        </thead>
        <tbody>
          {sampleDownloads.map((download) => (
            <tr key={download.id}>
              <td>{download.product}</td>
              <td>{download.expires}</td>
              <td>
                <DownloadButton href={download.link}>
                  Download File
                </DownloadButton>
              </td>
            </tr>
          ))}
        </tbody>
      </DownloadsTable>
    </div>
  );
};

export default DownloadsPage;
