// src/App.tsx
import React, { useEffect } from 'react';
import { ThemeProvider } from 'styled-components';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { UserProvider, useUser } from './context/UserContext'; // Import the UserProvider

import { GlobalStyle } from './styles/GlobalStyle';
import { theme } from './styles/theme';

import MainLayout from './components/common/MainLayout';
import DashboardPage from './pages/DashboardPage';
import OrdersPage from './pages/OrdersPage';
import OrderDetailPage from './pages/OrderDetailPage';
import DownloadsPage from './pages/DownloadsPage';
import AddressesPage from './pages/AddressesPage';
import AccountDetailsPage from './pages/AccountDetailsPage';
import SimulationPage from './pages/SimulationPage';

// This is a new component that handles the token logic
const AppInitializer = () => {
  const { setUser } = useUser();

  useEffect(() => {
    // Check for the auth_token in the URL
    const urlParams = new URLSearchParams(window.location.search);
    const token = urlParams.get('auth_token');

    if (token) {
      try {
        // Decode the token (it's base64 encoded)
        const decodedUserData = JSON.parse(atob(token));
        // Save the user data in our context
        setUser(decodedUserData);
        // Clean the token from the URL bar
        window.history.replaceState({}, document.title, window.location.pathname);
      } catch (e) {
        console.error("Failed to decode auth token:", e);
      }
    }
  }, [setUser]);

  return null; // This component doesn't render anything itself
}

function App() {
  return (
    // We wrap the entire app in the UserProvider
    <UserProvider>
      <ThemeProvider theme={theme}>
        <GlobalStyle />
        <Router>
          <AppInitializer /> {/* Add the token handler here */}
          <MainLayout>
            <Routes>
              <Route path="/" element={<DashboardPage />} />
              <Route path="/orders" element={<OrdersPage />} />
              <Route path="/orders/:orderId" element={<OrderDetailPage />} />
              <Route path="/downloads" element={<DownloadsPage />} />
              <Route path="/addresses" element={<AddressesPage />} />
              <Route path="/account-details" element={<AccountDetailsPage />} />
              <Route path="/practice/:fileId" element={<SimulationPage />} />
            </Routes>
          </MainLayout>
        </Router>
      </ThemeProvider>
    </UserProvider>
  );
}

export default App;
