// src/components/common/Header.tsx
import React from 'react';
import styled from 'styled-components';
import logoWhite from '../../assets/images/logo-white.png';

// The props interface now includes a function for the menu click
interface HeaderProps {
  onMenuClick: () => void;
}

const HeaderContainer = styled.header`
  background-color: ${({ theme }) => theme.colors.primary};
  height: 70px;
  padding: 0 24px;
  display: flex;
  justify-content: space-between;
  align-items: center;
  color: ${({ theme }) => theme.colors.textOnPrimary};
  /* This ensures the header stays on top of other content if needed */
  position: relative;
  z-index: 10;
`;

const Logo = styled.img`
  height: 38px;
`;

const WebsiteButton = styled.button`
  background-color: ${({ theme }) => theme.colors.button};
  color: ${({ theme }) => theme.colors.primary};
  border: none;
  border-radius: ${({ theme }) => theme.borderRadius.small};
  padding: 10px 16px;
  font-weight: 600;
  font-size: 14px;
  cursor: pointer;
  display: flex;
  align-items: center;
  gap: 8px;
  transition: background-color 0.2s ease-in-out;

  &:hover {
    background-color: #e8d9f5; /* A slightly darker shade for hover */
  }

  @media (max-width: 650px) {
    display: none;
  }
`;

const HamburgerButton = styled.button`
  display: none;
  background: none;
  border: none;
  color: ${({ theme }) => theme.colors.textOnPrimary};
  cursor: pointer;
  padding: 0;

  @media (max-width: 650px) {
    display: block;
    z-index: 1100; /* Ensure it's on top of the overlay */
  }
`;

// The component now accepts the onMenuClick prop
const Header: React.FC<HeaderProps> = ({ onMenuClick }) => {
  return (
    <HeaderContainer>
      <Logo src={logoWhite} alt="Cert Empire" />
      <WebsiteButton>
        &larr; Main Website
      </WebsiteButton>
      {/* The onClick now calls the function passed from the parent */}
      <HamburgerButton onClick={onMenuClick}>
        <svg 
          width="30" 
          height="30" 
          viewBox="0 0 24 24" 
          fill="none" 
          xmlns="http://www.w3.org/2000/svg"
        >
          <path 
            d="M4 6H20M4 12H20M4 18H20" 
            stroke="currentColor" 
            strokeWidth="2" 
            strokeLinecap="round" 
            strokeLinejoin="round"
          />
        </svg>
      </HamburgerButton>
    </HeaderContainer>
  );
};

export default Header;
