// src/pages/OrdersPage.tsx
import React from 'react';
import styled from 'styled-components';
import { Link } from 'react-router-dom';

// --- Sample Data ---
const sampleOrders = [
  { id: '70422', date: 'June 15, 2024', status: 'Completed', total: '$199.00 for 1 item' },
  { id: '68420', date: 'March 2, 2024', status: 'Completed', total: '$249.00 for 1 item' },
  { id: '68269', date: 'February 25, 2024', status: 'Completed', total: '$149.00 for 1 item' },
];

// --- Styled Components ---
const PageHeader = styled.h2`
  font-size: 24px;
  font-weight: 600;
  margin-bottom: 24px;
`;

const OrdersTable = styled.table`
  width: 100%;
  border-collapse: collapse;
  background-color: ${({ theme }) => theme.colors.surface};
  border: 1px solid ${({ theme }) => theme.colors.lightGrey};
  border-radius: 8px;
  overflow: hidden; /* Ensures the border-radius is respected by child elements */

  th, td {
    padding: 16px 24px;
    text-align: left;
    border-bottom: 1px solid ${({ theme }) => theme.colors.lightGrey};
  }

  th {
    background-color: #fafafa;
    font-weight: 600;
  }

  /* Remove border from the last row */
  tr:last-child td {
    border-bottom: none;
  }
`;

const ViewButton = styled(Link)`
  background-color: ${({ theme }) => theme.colors.primary};
  color: white;
  padding: 8px 16px;
  border-radius: 6px;
  text-decoration: none;
  font-weight: 500;
  transition: background-color 0.2s;

  &:hover {
    background-color: ${({ theme }) => theme.colors.primaryDark};
  }
`;

const OrdersPage = () => {
  return (
    <div>
      <PageHeader>Orders</PageHeader>
      <OrdersTable>
        <thead>
          <tr>
            <th>Order</th>
            <th>Date</th>
            <th>Status</th>
            <th>Total</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {sampleOrders.map((order) => (
            <tr key={order.id}>
              <td>#{order.id}</td>
              <td>{order.date}</td>
              <td>{order.status}</td>
              <td>{order.total}</td>
              <td>
                <ViewButton to={`/orders/${order.id}`}>View</ViewButton>
              </td>
            </tr>
          ))}
        </tbody>
      </OrdersTable>
    </div>
  );
};

export default OrdersPage;
