// src/pages/DownloadsPage.tsx
import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { Link } from 'react-router-dom';
import { supabase } from '../supabaseClient';
import { useUser } from '../context/UserContext';

// This type defines the shape of a download coming from the WooCommerce API
type WCDownload = {
  product_id: number;
  product_name: string;
  download_url: string;
  supabase_file_id: string; // Our custom field for the practice link
};

// --- Styled Components ---
const PageHeader = styled.h2` font-size: 24px; font-weight: 600; margin-bottom: 24px; `;
const DownloadsTable = styled.div` background-color: white; border-radius: 16px; border: 1px solid #EAEAEA; overflow: hidden; `;
const Row = styled.div` display: grid; grid-template-columns: 3fr 1fr; align-items: center; padding: 16px 24px; border-bottom: 1px solid #f0f0f0; &:last-child { border-bottom: none; } `;
const HeaderRow = styled(Row)` background-color: #fafafa; font-weight: 600; color: #333; `;
const DataCell = styled.div` color: #555; `;
const ButtonContainer = styled.div` display: flex; gap: 12px; justify-content: flex-end; `;
const PrimaryButton = styled.a` display: inline-block; background-color: #2c2c54; color: white; padding: 10px 20px; border-radius: 8px; text-decoration: none; font-weight: 500; font-size: 14px; `;
const SecondaryButton = styled(Link)` display: inline-block; background-color: white; color: #2c2c54; border: 1px solid #2c2c54; padding: 10px 20px; border-radius: 8px; text-decoration: none; font-weight: 500; font-size: 14px;`;
const MessageContainer = styled.div` padding: 40px; text-align: center; color: #757575; background-color: white; border-radius: 12px; border: 1px solid #EAEAEA; `;

const DownloadsPage = () => {
  const { user, isLoading: isUserLoading } = useUser();
  const [downloads, setDownloads] = useState<WCDownload[]>([]);
  const [isPageLoading, setPageLoading] = useState(true);

  useEffect(() => {
    const getPurchasedDownloads = async () => {
      // We must have a user with a WordPress ID to continue
      if (!user || !user.id) {
        setPageLoading(false);
        return;
      }

      try {
        // We call our new 'get-wc-data' function
        const { data, error } = await supabase.functions.invoke('get-wc-data', {
          body: { 
            endpoint: 'downloads', // Ask for the 'downloads' endpoint
            user_id: user.id       // Pass the WordPress user ID
          },
        });

        if (error) throw error;

        // The API returns a flat array of download objects
        if (Array.isArray(data)) {
            setDownloads(data);
        }

      } catch (error) {
        console.error('Error fetching downloads from WordPress:', error);
      } finally {
        setPageLoading(false);
      }
    };

    // We wait for the User Context to finish loading before we fetch data
    if (!isUserLoading) {
      getPurchasedDownloads();
    }
  }, [isUserLoading, user]);

  if (isUserLoading || isPageLoading) return <MessageContainer>Loading your downloads...</MessageContainer>;
  if (!user) return <MessageContainer>Please log in to view your downloads.</MessageContainer>;

  return (
    <div>
      <PageHeader>Downloads</PageHeader>
      <DownloadsTable>
        <HeaderRow>
          <DataCell>Product</DataCell>
          <DataCell style={{ textAlign: 'right' }}>Actions</DataCell>
        </HeaderRow>
        {downloads.length > 0 ? downloads.map((download) => (
          <Row key={download.download_id}>
            <DataCell>{download.product_name}</DataCell>
            <DataCell>
              <ButtonContainer>
                {/* The Download button now uses the real download_url from the API */}
                <PrimaryButton href={download.download_url} target="_blank">Download</PrimaryButton>

                {/* The Practice button uses the supabase_file_id, if it exists */}
                {download.supabase_file_id && (
                    <SecondaryButton to={`/practice/${download.supabase_file_id}`}>
                      Practice
                    </SecondaryButton>
                )}
              </ButtonContainer>
            </DataCell>
          </Row>
        )) : (
            <MessageContainer style={{border: 'none', borderRadius: 0}}>You have no available downloads.</MessageContainer>
        )}
      </DownloadsTable>
    </div>
  );
};

export default DownloadsPage;
