// src/pages/DownloadsPage.tsx
import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { Link } from 'react-router-dom';
import { useUser } from '../context/UserContext';
import { supabase } from '../supabaseClient'; // Assuming supabase is used

// --- Helper for UI ---



const formatDate = (isoTimestamp: string) => {
  try {
    return new Date(isoTimestamp).toLocaleDateString('en-US', {
      month: 'long',
      day: 'numeric',
      year: 'numeric',
    });
  } catch {
    return 'N/A';
  }
};

// --- Styled Components for Single Download Card ---

const SingleDownloadCard = styled.div`
  background: ${({ theme }) => theme.colors.surface};
  border-radius: 18px;
  padding: 36px 54px;
  display: flex;
  align-items: center;
  box-shadow: 0 4px 32px #00000014;
  margin: 40px auto 24px auto;
  justify-content: flex-start;
  max-width: 800px;
  min-height: 180px;
  position: relative;

  @media (max-width: 768px) {
    flex-direction: column;
    padding: 24px;
    text-align: center;
  }
`;

const ProductImage = styled.img`
  width: 180px;
  height: 180px;
  object-fit: cover;
  margin-right: 34px;
  background: #f5f5f5;
  border-radius: 12px;
  box-shadow: 0 2px 14px #0000001a;
  flex-shrink: 0;

  @media (max-width: 768px) {
    margin-right: 0;
    margin-bottom: 24px;
    width: 160px; /* Smaller image on mobile */
    height: 160px;
  }
`;

const CardBadge = styled.div<{ $isPrimary?: boolean }>`
  position: absolute;
  top: ${({ $isPrimary }) => $isPrimary ? '8px' : '18px'};
  left: ${({ $isPrimary }) => $isPrimary ? '8px' : 'auto'};
  right: ${({ $isPrimary }) => $isPrimary ? 'auto' : '28px'};
  background: ${({ theme, $isPrimary }) => $isPrimary ? '#45ccbe' : theme.colors.button};
  color: ${({ theme, $isPrimary }) => $isPrimary ? '#fff' : theme.colors.primary};
  font-weight: 700;
  font-size: 12px;
  padding: 3px 9px;
  border-radius: 7px;
  z-index: 2;
  box-shadow: 0 1px 4px #0000001a;

  @media (max-width: 768px) {
    top: 16px;
    left: ${({ $isPrimary }) => $isPrimary ? '16px' : 'auto'};
    right: ${({ $isPrimary }) => $isPrimary ? 'auto' : '16px'};
  }
`;

const InfoContainer = styled.div`
  flex: 1;
`;

const ProductName = styled.div`
  font-size: 22px;
  font-weight: 700;
  color: ${({ theme }) => theme.colors.primary};
  margin-bottom: 5px;

  @media (max-width: 768px) {
    font-size: 20px; /* Smaller font on mobile */
  }
`;

const MetaInfo = styled.div`
  color: #6b6b8d;
  font-size: 15px;
  margin-bottom: 7px;
  font-weight: 500;
  display: flex; /* Use flexbox for alignment */
  flex-wrap: wrap; /* Allow wrapping on small screens */
  gap: 0 18px; /* Horizontal gap between items */

  b {
    color: ${({ theme }) => theme.colors.textPrimary};
  }

  @media (max-width: 768px) {
    flex-direction: column;
    gap: 8px; /* Vertical gap on mobile */
    align-items: center;
  }
`;

const OrderInfo = styled.div`
  color: #444;
  font-size: 13px;
  margin: 6px 0 0 0;
`;

const ActionButtons = styled.div`
  margin-top: 19px;
  display: flex;
  gap: 14px;

  @media (max-width: 768px) {
    justify-content: center;
    flex-direction: column; /* Stack buttons vertically */
    align-items: stretch; /* Make buttons full width */
  }
`;

const FooterInfo = styled.div`
  margin-top: 16px;
  color: #a0a0c0;
  font-size: 14px;
`;

const HelpLinks = styled.div`
  margin-top: 22px;
  text-align: center;
  color: #6b6b8d;
  font-size: 15px;

  a {
    color: ${({ theme }) => theme.colors.primary};
    text-decoration: underline;
    font-weight: 500;
  }
`;

// --- Original Table Styles (Unchanged) ---
const PageHeader = styled.h2`
  font-size: 24px;
  font-weight: 600;
  margin-bottom: 24px;
`;
const DownloadsTable = styled.div`
  background-color: white;
  border-radius: 16px;
  border: 1px solid #EAEAEA;
  overflow: hidden;
`;
const Row = styled.div`
  display: grid;
  grid-template-columns: 3fr 1fr 1fr 2fr;
  align-items: center;
  padding: 16px 24px;
  border-bottom: 1px solid #f0f0f0;
  &:last-child {
    border-bottom: none;
  }
`;
const HeaderRow = styled(Row)`
