// src/components/common/Sidebar.tsx
import React from 'react';
import styled from 'styled-components';
import { NavLink } from 'react-router-dom';
import { useUser } from '../../context/UserContext';

// This props interface is for the mobile menu functionality
interface SidebarProps {
  isOpen: boolean;
}

const SidebarContainer = styled.aside<{ isOpen: boolean; }>`
  width: 260px;
  background-color: white;
  padding: 16px;
  border-right: 1px solid #e9e9e9;
  flex-shrink: 0;

  @media (max-width: 650px) {
    position: fixed;
    left: 0;
    top: 0;
    height: 100%;
    margin: 0;
    border-radius: 0;
    transform: ${({ isOpen }) => isOpen ? 'translateX(0)' : 'translateX(-100%)'};
    transition: transform 0.3s ease-in-out;
    z-index: 1000;
    box-shadow: 0 0 15px rgba(0,0,0,0.2);
  }
`;

const NavList = styled.nav`
  display: flex;
  flex-direction: column;
  gap: 8px;
`;

const StyledNavLink = styled(NavLink)`
  padding: 14px 20px;
  color: #757575; /* textSecondary */
  text-decoration: none;
  font-weight: 500;
  transition: all 0.2s ease-in-out;
  border-radius: 8px;

  &.active {
    background-color: #EFEAFE;
    color: #673AB7;
  }

  &:not(.active):hover {
    background-color: #f5f5f5;
  }
`;

const LogoutButton = styled.button`
  width: 100%;
  padding: 14px 20px;
  color: #757575;
  font-size: 16px;
  font-weight: 500;
  font-family: inherit;
  background: none;
  border: none;
  border-radius: 8px;
  cursor: pointer;
  text-align: left;
  margin-top: 8px; 

  &:hover {
    background-color: #f5f5f5; 
  }
`;

const Sidebar: React.FC<SidebarProps> = ({ isOpen }) => {
  const { logout } = useUser();

  return (
    <SidebarContainer isOpen={isOpen}>
      <NavList>
        <StyledNavLink to="/" end>Dashboard</StyledNavLink>
        <StyledNavLink to="/orders">Orders</StyledNavLink>
        <StyledNavLink to="/downloads">Downloads</StyledNavLink>
        <StyledNavLink to="/addresses">Addresses</StyledNavLink>
        <StyledNavLink to="/account-details">Account Details</StyledNavLink>
        <LogoutButton onClick={logout}>Log out</LogoutButton>
      </NavList>
    </SidebarContainer>
  );
}; // The extra brace was here, now it is removed.

export default Sidebar;
