// src/pages/DownloadsPage.tsx
import React from 'react';
import styled from 'styled-components';
import { Link } from 'react-router-dom'; // Import Link for navigation

// --- Sample Data ---
const sampleDownloads = [
  { id: 'd1', product: 'CISSP - Certified Information Systems Security Professional', expires: 'Never', link: '#' },
  { id: 'd2', product: 'CompTIA Security+ (SY0-601)', expires: 'Never', link: '#' },
  { id: 'd3', product: 'Certified Ethical Hacker (CEH) v11', expires: 'October 25, 2025', link: '#' },
];

// --- Styled Components ---
const PageHeader = styled.h2`
  font-size: 24px;
  font-weight: 600;
  margin-bottom: 24px;
`;

const DownloadsTable = styled.table`
  width: 100%;
  border-collapse: collapse;
  background-color: ${({ theme }) => theme.colors.surface};
  border: 1px solid ${({ theme }) => theme.colors.lightGrey};
  border-radius: 8px;
  overflow: hidden;

  th, td {
    padding: 16px 24px;
    text-align: left;
    border-bottom: 1px solid ${({ theme }) => theme.colors.lightGrey};
    white-space: nowrap; /* Prevents buttons from wrapping on smaller screens */
  }

  th {
    background-color: #fafafa;
    font-weight: 600;
  }

  tr:last-child td {
    border-bottom: none;
  }
`;

const ButtonContainer = styled.div`
  display: flex;
  gap: 12px; /* Adds space between the buttons */
`;

const DownloadButton = styled.a`
  display: inline-block;
  background-color: ${({ theme }) => theme.colors.primary};
  color: white;
  padding: 8px 16px;
  border-radius: 6px;
  text-decoration: none;
  font-weight: 500;
  transition: background-color 0.2s;

  &:hover {
    background-color: ${({ theme }) => theme.colors.primaryDark};
  }
`;

// New style for the "Practice" button
const PracticeButton = styled(Link)`
  display: inline-block;
  background-color: ${({ theme }) => theme.colors.surface};
  color: ${({ theme }) => theme.colors.primary};
  border: 1px solid ${({ theme }) => theme.colors.primary};
  padding: 8px 16px;
  border-radius: 6px;
  text-decoration: none;
  font-weight: 500;
  transition: all 0.2s;

  &:hover {
    background-color: ${({ theme }) => theme.colors.primary};
    color: white;
  }
`;


const DownloadsPage = () => {
  return (
    <div>
      <PageHeader>Downloads</PageHeader>
      <DownloadsTable>
        <thead>
          <tr>
            <th>Product</th>
            <th>Expires</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {sampleDownloads.map((download) => (
            <tr key={download.id}>
              <td>{download.product}</td>
              <td>{download.expires}</td>
              <td>
                {/* Container for both buttons */}
                <ButtonContainer>
                  <DownloadButton href={download.link}>
                    Download File
                  </DownloadButton>
                  {/* The new button that links to the practice page */}
                  <PracticeButton to="/practice">
                    Practice
                  </PracticeButton>
                </ButtonContainer>
              </td>
            </tr>
          ))}
        </tbody>
      </DownloadsTable>
    </div>
  );
};

export default DownloadsPage;
