import React, { useState, useEffect, useMemo } from 'react';
import styled from 'styled-components';
import { useParams } from 'react-router-dom';
import { supabase } from '../supabaseClient';
import HtmlRenderer from '../components/utils/HtmlRenderer';

// Icon Components (Removed invalid bracketed URLs)
const IconSearch = () => (
  <svg width="20" height="20" viewBox="0 0 15 15" fill="none" xmlns="http://www.w3.org/2000/svg">
    <path
      d="M10 6.5C10 8.433 8.433 10 6.5 10C4.567 10 3 8.433 3 6.5C3 4.567 4.567 3 6.5 3C8.433 3 10 4.567 10 6.5ZM9.30884 10.0159L11.5 12.5L12.5 11.5L10.0158 9.30885C9.42248 9.75587 8.68594 10.0345 7.89932 10.0883L6.5 10C4.567 10 3 8.433 3 6.5C3 4.567 4.567 3 6.5 3C8.433 3 10 4.567 10 6.5C10 7.50556 9.61334 8.42373 9.00001 9.13098L9.30884 10.0159Z"
      fill="currentColor"
    />
  </svg>
);
const IconFlag = () => (
  <svg width="16" height="16" viewBox="0 0 15 15" fill="none" xmlns="http://www.w3.org/2000/svg">
    <path d="M4.5 14.5V4.5H12.5L9.5 7L12.5 9.5H4.5ZM3 3.5H14V10.5L10 7.5L14 4.5H4V14.5H3V3.5Z" fill="currentColor" />
  </svg>
);
const IconDownload = () => (
  <svg width="18" height="18" viewBox="0 0 15 15" fill="none" xmlns="http://www.w3.org/2000/svg">
    <path
      d="M4 6H2V12.5C2 12.7761 2.22386 13 2.5 13H12.5C12.7761 13 13 12.7761 13 12.5V6H11V12H4V6ZM7.5 10L11 5H8V2H7V5H4L7.5 10Z"
      fill="currentColor"
    />
  </svg>
);
const IconEye = () => (
  <svg width="16" height="16" viewBox="0 0 15 15" fill="none" xmlns="http://www.w3.org/2000/svg">
    <path
      d="M7.5 11C4.79322 11 2.49241 9.2993 1.05532 7.50001C2.49241 5.70072 4.79322 4 7.5 4C10.2068 4 12.5076 5.70072 13.9447 7.50001C12.5076 9.2993 10.2068 11 7.5 11ZM7.5 3C4.33776 3 1.68988 5.00373 0.160645 6.88398L0.116041 6.94165C-0.0386803 7.14378 -0.0386803 7.85624 0.116041 8.05837L0.160645 8.11604C1.68988 9.99629 4.33776 12 7.5 12C10.6622 12 13.3101 9.99629 14.8394 8.11604L14.884 8.05837C15.0387 7.85624 15.0387 7.14378 14.884 6.94165L14.8394 6.88398C13.3101 5.00373 10.6622 3 7.5 3ZM7.5 9C6.40237 9 5.50655 8.11289 5.5 7.00001C5.50655 5.88713 6.40237 5.00001 7.5 5.00001C8.59763 5.00001 9.49345 5.88713 9.5 7.00001C9.49345 8.11289 8.59763 9 7.5 9Z"
      fill="currentColor"
    />
  </svg>
);
const IconEyeSlash = () => (
  <svg width="16" height="16" viewBox="0 0 15 15" fill="none" xmlns="http://www.w3.org/2000/svg">
    <path
      d="M1.3415 13.6585L13.6582 1.34176L12.9511 0.634691L0.634433 12.9514L1.3415 13.6585ZM4.39893 9.60107C3.13626 8.87784 2.15372 7.84628 1.05532 7.5C2.49241 5.70072 4.79322 4 7.5 4C8.61439 4 9.66427 4.29891 10.584 4.80878L9.49989 6.00001C9.49561 5.92548 9.24345 5.88713 9.5 7C9.49345 8.11289 8.59763 9 7.5 9C6.81406 9 6.20237 8.68594 5.86902 8.13098L4.39893 9.60107ZM14.8394 6.88398C13.5936 5.25338 11.6622 3 7.5 3C6.59763 3 5.75655 3.20237 5.00001 3.55837L6.50001 5C6.40237 5 5.50655 5.88713 5.5 7C5.50655 7.24345 5.57452 7.46548 5.68988 7.68988L7.584 9.584C8.75655 9.42548 9.49345 8.49345 9.5 7.41594L13.8839 12.8394C14.4934 12.1129 14.8394 11.2434 14.8394 10.5C14.8394 9.99629 13.3101 8.11604 14.884 8.05837C15.0387 7.85624 15.0387 7.14378 14.884 6.94165L14.8394 6.88398Z"
      fill="currentColor"
    />
  </svg>
);

// Type Definitions
type Question = { id: string; answer: string[]; options: string[]; question: string; explanation: string; question_number: string };
type Topic = { questions: Question[]; case_study?: string; topic_name?: string };
type SimulationData = { fileName: string; topics: Record<string, Topic> };
type SupabaseFile = { 'ai-parsed-data'?: { result?: { topics: Record<string, Topic> } }; filename: string };
type StructuredItem = { type: 'question'; content: Question; parent?: { topic?: string; case_study?: string } };

// Styled Components
const PageContainer = styled.div`
  display: flex;
  flex-direction: column;
  gap: 24px;
`;

const HeaderContainer = styled.div`
  display: flex;
  flex-direction: column;
  gap: 16px;
`;

const HeaderTopRow = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  gap: 16px;
  flex-wrap: wrap;
`;

const FileName = styled.h2`
  color: ${({ theme }) => theme.colors.primary};
  font-weight: 700;
  font-size: 24px;
  flex-grow: 1;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
  max-width: 100%; /* Prevent overflow */
`;

const HeaderActions = styled.div`
  display: flex;
  align-items: center;
  gap: 12px;
  flex-shrink: 0;
`;

const SearchInput = styled.div`
  display: flex;
  align-items: center;
  background-color: ${({ theme }) => theme.colors.surface};
  border: 1px solid ${({ theme }) => theme.colors.lightGrey};
  border-radius: 12px;
  padding: 0 12px;
  width: 250px;
  color: ${({ theme }) => theme.colors.textSecondary};

  input {
    border: none;
    outline: none;
    padding: 12px 8px;
    font-size: 14px;
    flex-grow: 1;
    background: transparent;
    color: ${({ theme }) => theme.colors.textPrimary};
  }

  @media (max-width: 650px) {
    width: 100%;
    order: 3;
  }
`;

const ActionButton = styled.button`
  background: ${({ theme }) => theme.colors.surface};
  border: 1px solid ${({ theme }) => theme.colors.lightGrey};
  padding: 8px 12px;
  border-radius: 12px;
  cursor: pointer;
  display: flex;
  align-items: center;
  gap: 8px;
  font-weight: 600;
  color: ${({ theme }) => theme.colors.textSecondary};
  transition: all 0.2s ease-in-out;

  &:hover {
    border-color: ${({ theme }) => theme.colors.primary};
    color: ${({ theme }) => theme.colors.primary};
  }
`;

const DownloadButton = styled(ActionButton)`
  background-color: ${({ theme }) => theme.colors.primary};
  color: ${({ theme }) => theme.colors.textOnPrimary};
`;

const GoToFieldContainer = styled.div<{ $show: boolean }>`
  max-height: ${({ $show }) => ($show ? '60px' : '0')};
  display: flex;
  justify-content: center;
  align-items: center;
  gap: 8px;
  padding: ${({ $show }) => ($show ? '8px 0' : '0')};
  overflow: hidden;
  transition: all 0.3s ease-in-out;
  opacity: ${({ $show }) => ($show ? '1' : '0')};

  input {
    width: 100px;
    text-align: center;
    padding: 8px;
    border-radius: 8px;
    border: 1px solid ${({ theme }) => theme.colors.lightGrey};
    &:focus {
      outline: none;
      border-color: ${({ theme }) => theme.colors.primary};
    }
  }
`;

const ContentContainer = styled.div`
  background-color: ${({ theme }) => theme.colors.surface};
  border-radius: 18px;
  border: 1px solid ${({ theme }) => theme.colors.lightGrey};
  box-shadow: ${({ theme }) => theme.shadows.card};
  overflow-wrap: break-word;
  word-wrap: break-word;
`;

const CaseStudyContainer = styled.div`
  background-color: rgba(45, 22, 132, 0.05);
  border-bottom: 1px solid rgba(45, 22, 132, 0.1);
  padding: 26px;
  border-radius: 18px 18px 0 0;
`;

const QuestionCardContainer = styled.div`
  padding: 26px;
  @media (max-width: 600px) {
    padding: 16px;
  }
`;

const QuestionHeader = styled.div`
  display: flex;
  align-items: flex-start;
  gap: 18px;
  margin-bottom: 24px;
  @media (max-width: 600px) {
    flex-direction: column;
    gap: 16px;
  }
`;

const QuestionNumber = styled.div`
  background-color: rgba(45, 22, 132, 0.12);
  color: ${({ theme }) => theme.colors.primary};
  font-weight: 700;
  font-size: 16px;
  padding: 12px;
  border-radius: 10px;
  flex-shrink: 0;
`;

const Label = styled.p`
  color: ${({ theme }) => theme.colors.textSecondary};
  font-weight: 600;
  font-size: 12px;
  text-transform: uppercase;
  letter-spacing: 0.8px;
  margin-bottom: 8px;
`;

const OptionsList = styled.div`
  display: flex;
  flex-direction: column;
  gap: 12px;
  padding-left: 76px;
  @media (max-width: 600px) {
    padding-left: 0;
  }
`;

const OptionItem = styled.div<{ $isCorrect: boolean }>`
  border: 1.1px solid ${({ theme, $isCorrect }) => ($isCorrect ? theme.colors.success : theme.colors.lightGrey)};
  background-color: ${({ theme, $isCorrect }) => ($isCorrect ? 'rgba(76, 175, 80, 0.1)' : 'transparent')};
  border-radius: 8px;
  padding: 11px 14px;
  font-size: 15px;
  line-height: 1.5;
  transition: all 0.2s ease-in-out;
  overflow-wrap: break-word;
  word-wrap: break-word;

  b {
    font-weight: 600;
  }
`;

const ActionButtons = styled.div`
  display: flex;
  justify-content: flex-end;
  gap: 8px;
  margin-top: 26px;
`;

const ShowAnswerButton = styled(ActionButton)`
  background-color: ${({ theme }) => theme.colors.primary};
  color: ${({ theme }) => theme.colors.textOnPrimary};
  &:hover {
    background-color: ${({ theme }) => theme.colors.primary};
    color: ${({ theme }) => theme.colors.textOnPrimary};
  }
`;

const ExplanationSection = styled.div`
  padding-top: 24px;
  margin-top: 24px;
  border-top: 1.1px solid ${({ theme }) => theme.colors.lightGrey};
`;

const PaginationContainer = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 16px 24px;
  background-color: ${({ theme }) => theme.colors.surface};
  border: 1px solid ${({ theme }) => theme.colors.lightGrey};
  border-radius: 12px;
  box-shadow: ${({ theme }) => theme.shadows.card};
`;

const PageButton = styled.button`
  background: none;
  border: 1px solid ${({ theme }) => theme.colors.lightGrey};
  color: ${({ theme }) => theme.colors.textSecondary};
  padding: 8px 16px;
  border-radius: 8px;
  cursor: pointer;
  transition: all 0.2s ease-in-out;

  &:hover:not(:disabled) {
    border-color: ${({ theme }) => theme.colors.primary};
    color: ${({ theme }) => theme.colors.primary};
  }

  &:disabled {
    cursor: not-allowed;
    opacity: 0.5;
  }
`;

const LoadingContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 300px;
  font-size: 18px;
  color: ${({ theme }) => theme.colors.textSecondary};
`;

const ErrorContainer = styled(LoadingContainer)`
  color: ${({ theme }) => theme.colors.error || 'red'};
`;

const SimulationPage = () => {
  const { fileId } = useParams<{ fileId: string }>();
  const [simulationData, setSimulationData] = useState<SimulationData | null>(null);
  const [structuredItems, setStructuredItems] = useState<StructuredItem[]>([]);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [isLoading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [showAnswer, setShowAnswer] = useState(false);
  const [showGoTo, setShowGoTo] = useState(false);
  const [goToValue, setGoToValue] = useState('');
  const [searchQuery, setSearchQuery] = useState('');

  useEffect(() => {
    if (!fileId) {
      setError('Invalid file ID');
      setLoading(false);
      return;
    }
    async function getSimulationData() {
      setLoading(true);
      setError(null);
      const { data, error } = await supabase.from('files').select('filename, ai-parsed-data').eq('id', fileId).single<SupabaseFile>();
      if (error || !data || !data['ai-parsed-data']?.result?.topics) {
        setError('Failed to load simulation data');
        console.error('Error fetching file:', error);
      } else {
        const topics = data['ai-parsed-data'].result.topics;
        setSimulationData({ fileName: data.filename, topics });
        const newStructuredItems: StructuredItem[] = [];
        let qNum = 1;
        Object.values(topics).forEach((topic: Topic) => {
          (topic.questions || []).forEach((q: Question) => {
            newStructuredItems.push({
              type: 'question',
              content: { ...q, question_number: q.question_number || (qNum++).toString(), id: q.id || `q-${qNum}` },
              parent: { topic: topic.topic_name, case_study: topic.case_study },
            });
          });
        });
        setStructuredItems(newStructuredItems);
      }
      setLoading(false);
    }
    getSimulationData();
  }, [fileId]);

  useEffect(() => {
    setShowAnswer(false);
  }, [currentIndex]);

  const handleNext = () => setCurrentIndex((prev) => Math.min(prev + 1, filteredItems.length - 1));
  const handlePrev = () => setCurrentIndex((prev) => Math.max(prev - 1, 0));

  const handleGoTo = () => {
    const num = parseInt(goToValue, 10);
    if (!isNaN(num) && num > 0 && num <= filteredItems.length) {
      const itemIndex = filteredItems.findIndex((item) => parseInt(item.content.question_number, 10) === num);
      if (itemIndex !== -1) {
        setCurrentIndex(itemIndex);
      }
    }
    setGoToValue('');
    setShowGoTo(false);
  };

  const handleDownload = () => {
    // Implement download logic here (e.g., generate and download a file)
    console.log('Download initiated for:', simulationData?.fileName);
  };

  const filteredItems = useMemo(() => {
    if (!searchQuery) return structuredItems;
    return structuredItems.filter((item) =>
      item.type === 'question' && item.content.question.toLowerCase().includes(searchQuery.toLowerCase())
    );
  }, [searchQuery, structuredItems]);

  const currentItem = filteredItems[currentIndex];

  if (isLoading) return <LoadingContainer>Loading Simulation...</LoadingContainer>;
  if (error) return <ErrorContainer>{error}</ErrorContainer>;
  if (!structuredItems.length) return <LoadingContainer>Could not load questions for this file.</LoadingContainer>;

  return (
    <PageContainer>
      <HeaderContainer>
        <HeaderTopRow>
          <FileName title={simulationData?.fileName}>{simulationData?.fileName}</FileName>
          <HeaderActions>
            <SearchInput>
              <IconSearch />
              <input
                type="text"
                placeholder="Search in this file..."
                value={searchQuery}
                onChange={(e) => {
                  setSearchQuery(e.target.value);
                  if (filteredItems.length > 0 && currentItem) {
                    const newIndex = filteredItems.findIndex(
                      (item) => item.content.id === currentItem.content.id
                    );
                    setCurrentIndex(newIndex !== -1 ? newIndex : 0);
                  }
                }}
              />
            </SearchInput>
            <ActionButton onClick={() => setShowGoTo(!showGoTo)}>Go To</ActionButton>
            <DownloadButton onClick={handleDownload}>
              <IconDownload />
              Download
            </DownloadButton>
          </HeaderActions>
        </HeaderTopRow>
        <GoToFieldContainer $show={showGoTo}>
          <input
            type="number"
            placeholder="Q #"
            value={goToValue}
            onChange={(e) => setGoToValue(e.target.value)}
            onKeyDown={(e) => e.key === 'Enter' && handleGoTo()}
          />
          <ActionButton onClick={handleGoTo}>Go</ActionButton>
        </GoToFieldContainer>
      </HeaderContainer>

      {currentItem ? (
        <ContentContainer>
          {currentItem.parent?.case_study && (
            <CaseStudyContainer>
              <HtmlRenderer htmlString={currentItem.parent.case_study} />
            </CaseStudyContainer>
          )}
          <QuestionCardContainer>
            <QuestionHeader>
              <QuestionNumber>Q: {currentItem.content.question_number}</QuestionNumber>
              <HtmlRenderer htmlString={currentItem.content.question} />
            </QuestionHeader>
            <Label>Options</Label>
            <OptionsList>
              {currentItem.content.options.map((opt: string, index: number) => {
                const optionLetter = String.fromCharCode(65 + index);
                const isCorrect = showAnswer && currentItem.content.answer.includes(optionLetter);
                return (
                  <OptionItem key={index} $isCorrect={isCorrect}>
                    <b>{optionLetter}. </b>
                    <HtmlRenderer htmlString={opt.replace(/^[A-Z][.)]\s*/, '')} />
                  </OptionItem>
                );
              })}
            </OptionsList>
            <ActionButtons>
              <ActionButton>
                <IconFlag /> Report
              </ActionButton>
              <ShowAnswerButton onClick={() => setShowAnswer(!showAnswer)}>
                {showAnswer ? <IconEyeSlash /> : <IconEye />}
                {showAnswer ? 'Hide Answer' : 'Show Answer'}
              </ShowAnswerButton>
            </ActionButtons>
            {showAnswer && (
              <ExplanationSection>
                <Label>Explanation</Label>
                <HtmlRenderer htmlString={currentItem.content.explanation} />
              </ExplanationSection>
            )}
          </QuestionCardContainer>
        </ContentContainer>
      ) : (
        <LoadingContainer>No questions found for your search.</LoadingContainer>
      )}

      <PaginationContainer>
        <span>Question {currentItem ? currentItem.content.question_number : 0} of {filteredItems.length}</span>
        <div style={{ display: 'flex', gap: '8px' }}>
          <PageButton onClick={handlePrev} disabled={currentIndex === 0}>
            Back
          </PageButton>
          <PageButton onClick={handleNext} disabled={currentIndex >= filteredItems.length - 1}>
            Next
          </PageButton>
        </div>
      </PaginationContainer>
    </PageContainer>
  );
};

export default SimulationPage;
