// src/pages/AccountDetailsPage.tsx
import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { useUser } from '../context/UserContext';
import { supabase } from '../supabaseClient';
import { Link, useNavigate } from 'react-router-dom';

// Type for the form data
type AddressFormData = {
  first_name: string;
  last_name: string;
  address_1: string;
  city: string;
  state: string;
  postcode: string;
  country: string;
};

// --- Styled Components ---
const PageHeader = styled.h2` font-size: 24px; font-weight: 600; margin-bottom: 24px; `;
const FormContainer = styled.form` background-color: white; border: 1px solid #EAEAEA; border-radius: 8px; padding: 24px; `;
const FormGroup = styled.div` margin-bottom: 20px; `;
const FormLabel = styled.label` display: block; font-weight: 500; margin-bottom: 8px; `;
const FormInput = styled.input` width: 100%; padding: 12px; border: 1px solid #ccc; border-radius: 4px; font-size: 16px; `;
const SaveButton = styled.button` background-color: #2c2c54; color: white; padding: 12px 24px; border-radius: 8px; border: none; font-size: 16px; font-weight: 500; cursor: pointer; &:disabled { background-color: #ccc; } `;
const LoadingContainer = styled.div` padding: 40px; text-align: center; color: #757575; `;
const BackLink = styled(Link)` display: inline-block; margin-bottom: 24px; color: #2c2c54; text-decoration: none; font-weight: 500; `;


const AccountDetailsPage = () => {
  const { user } = useUser();
  const navigate = useNavigate();
  const [formData, setFormData] = useState<AddressFormData | null>(null);
  const [isLoading, setLoading] = useState(true);
  const [isSaving, setSaving] = useState(false);

  // 1. Fetch current data to pre-fill the form
  useEffect(() => {
    if (!user) return;
    const fetchCurrentData = async () => {
      setLoading(true);
      const { data: apiResponse } = await supabase.functions.invoke('get-wc-data', {
        body: { method: 'GET', endpoint: `cwc/customer/${user.id}`, user_id: user.id },
      });
      if (apiResponse && apiResponse.Success) {
        setFormData(apiResponse.Data.billing); // We'll just edit the billing address for this example
      }
      setLoading(false);
    };
    fetchCurrentData();
  }, [user]);

  // 2. Handle form input changes
  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    if (!formData) return;
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  // 3. Handle form submission
  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!user || !formData) return;

    setSaving(true);
    try {
      const { data, error } = await supabase.functions.invoke('get-wc-data', {
        body: {
          method: 'PUT',
          endpoint: `cwc/update-customer/${user.id}`,
          billing: formData, // Send the updated billing data
          // you could also include a 'shipping: { ... }' object here
        },
      });

      if (error) throw error;

      alert('Address updated successfully!');
      navigate('/addresses'); // Go back to the addresses page

    } catch (err) {
      console.error("Error updating address:", err);
      alert('Failed to update address.');
    } finally {
      setSaving(false);
    }
  };

  if (isLoading || !formData) return <LoadingContainer>Loading Account Details...</LoadingContainer>;

  return (
    <div>
      <BackLink to="/addresses">← Back to Addresses</BackLink>
      <PageHeader>Edit Billing Address</PageHeader>
      <FormContainer onSubmit={handleSubmit}>
        <FormGroup>
          <FormLabel>First Name</FormLabel>
          <FormInput name="first_name" value={formData.first_name} onChange={handleInputChange} />
        </FormGroup>
        <FormGroup>
          <FormLabel>Last Name</FormLabel>
          <FormInput name="last_name" value={formData.last_name} onChange={handleInputChange} />
        </FormGroup>
        <FormGroup>
          <FormLabel>Street Address</FormLabel>
          <FormInput name="address_1" value={formData.address_1} onChange={handleInputChange} />
        </FormGroup>
        <FormGroup>
          <FormLabel>City</FormLabel>
          <FormInput name="city" value={formData.city} onChange={handleInputChange} />
        </FormGroup>
        <FormGroup>
          <FormLabel>State</FormLabel>
          <FormInput name="state" value={formData.state} onChange={handleInputChange} />
        </FormGroup>
        <FormGroup>
          <FormLabel>Postcode / ZIP</FormLabel>
          <FormInput name="postcode" value={formData.postcode} onChange={handleInputChange} />
        </FormGroup>
        <SaveButton type="submit" disabled={isSaving}>
          {isSaving ? 'Saving...' : 'Save Changes'}
        </SaveButton>
      </FormContainer>
    </div>
  );
};

export default AccountDetailsPage;
