// src/pages/DownloadsPage.tsx
import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { Link } from 'react-router-dom';

// --- Type definition for a file ---
type File = {
  id: string;
  filename: string;
  created_at: string;
};

// --- Styled Components (No changes needed) ---
const PageHeader = styled.h2` font-size: 24px; font-weight: 600; margin-bottom: 24px; `;
const DownloadsTable = styled.table` width: 100%; border-collapse: collapse; background-color: white; border: 1px solid #E0E0E0; border-radius: 8px; overflow: hidden; th, td { padding: 16px 24px; text-align: left; border-bottom: 1px solid #f0f0f0; } th { background-color: #fafafa; font-weight: 600; } tr:last-child td { border-bottom: none; } `;
const ButtonContainer = styled.div` display: flex; gap: 12px; `;
const PrimaryButton = styled(Link)` display: inline-block; background-color: #2c2c54; color: white; padding: 10px 20px; border-radius: 8px; text-decoration: none; font-weight: 500; `;
const SecondaryButton = styled(Link)` display: inline-block; background-color: white; color: #2c2c54; border: 1px solid #2c2c54; padding: 10px 20px; border-radius: 8px; text-decoration: none; font-weight: 500; `;
const MessageContainer = styled.div` padding: 40px; text-align: center; color: #757575; background-color: white; border-radius: 12px; border: 1px solid #EAEAEA; `;

const DownloadsPage = () => {
  const [files, setFiles] = useState<File[]>([]);
  const [isLoading, setLoading] = useState(true);
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  useEffect(() => {
    async function getPurchasedFiles() {
      // 1. Get the saved token from the browser's storage
      const token = localStorage.getItem('user_auth_token');

      if (!token) {
        console.log("No user token found. User is not logged in.");
        setIsLoggedIn(false);
        setLoading(false);
        return;
      }

      setIsLoggedIn(true);
      setLoading(true);

      try {
        // 2. Make a call to YOUR backend server with the token
        // =================================================================
        // IMPORTANT: Replace the URL below with your actual backend API endpoint
        // =================================================================
        const response = await fetch('https://YOUR_BACKEND_URL/api/get-user-files', {
          method: 'GET',
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json'
          }
        });

        if (!response.ok) {
          throw new Error(`Failed to fetch user files. Server responded with ${response.status}`);
        }

        const userData = await response.json();

        // Assuming your backend returns an object with a "files" array
        if (userData && Array.isArray(userData.files)) {
            setFiles(userData.files);
        }

      } catch (error) {
        console.error('Error fetching purchased files:', error);
        setFiles([]); // Clear files on error
      } finally {
        setLoading(false);
      }
    }

    getPurchasedFiles();
  }, []);

  if (isLoading) {
    return <MessageContainer>Loading your downloads...</MessageContainer>;
  }

  if (!isLoggedIn) {
      return <MessageContainer>Please log in to view your downloads.</MessageContainer>;
  }

  if (files.length === 0) {
      return <MessageContainer>You have no available downloads.</MessageContainer>;
  }

  return (
    <div>
      <PageHeader>Downloads</PageHeader>
      <DownloadsTable>
        <thead>
          <tr>
            <th>Product</th>
            <th>Date Added</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {files.map((file) => (
            <tr key={file.id}>
              <td>{file.filename}</td>
              <td>{new Date(file.created_at).toLocaleDateString()}</td>
              <td>
                <ButtonContainer>
                  <PrimaryButton to="#">Download File</PrimaryButton>
                  <SecondaryButton to={`/practice/${file.id}`}>
                    Practice
                  </SecondaryButton>
                </ButtonContainer>
              </td>
            </tr>
          ))}
        </tbody>
      </DownloadsTable>
    </div>
  );
};

export default DownloadsPage;
