// src/components/common/MainLayout.tsx
import React from 'react';
import styled from 'styled-components';
import Sidebar from './Sidebar';
import Header from './Header';
import Footer from './Footer';

const AppContainer = styled.div`
  /* This container uses Flexbox to place the sidebar next to the main content */
  display: flex;
  background-color: #f5f7fa; /* The light grey background */
`;

const PageWrapper = styled.div`
  /* This wrapper takes up the remaining space and will handle all scrolling */
  flex-grow: 1;
  height: 100vh;
  overflow-y: auto;
  display: flex;
  flex-direction: column; /* Stacks Header, Content, and Footer vertically */
`;

const ContentContainer = styled.main`
  /* This container holds the actual page content (like the Dashboard) */
  flex-grow: 1; /* This makes it push the footer down */
  padding: 24px 32px;
  width: 100%;
`;

interface MainLayoutProps {
  children: React.ReactNode;
}

const MainLayout: React.FC<MainLayoutProps> = ({ children }) => {
  return (
    <AppContainer>
      {/* The Sidebar is fixed on the left */}
      <Sidebar />

      {/* The PageWrapper contains everything on the right and scrolls */}
      <PageWrapper>
        <Header />
        <ContentContainer>
          {children}
        </ContentContainer>
        <Footer />
      </PageWrapper>
    </AppContainer>
  );
};

export default MainLayout;
