// src/pages/SimulationPage.tsx
import React, { useState } from 'react';
import styled from 'styled-components';

// --- Correct, Nested Mock Data ---
const mockData = {
  fileName: "MB-330: Microsoft Dynamics 365 Supply Chain Management",
  totalQuestions: 150,
  items: [
    { 
      type: 'topic' as const, 
      title: 'Configure and manage the supply chain in Dynamics 365',
      items: [
        {
          type: 'case_study' as const,
          title: 'Contoso Coffee Shop Supply Chain',
          description: 'Contoso Coffee needs to manage its inventory...',
          questions: [
            {
              type: 'question' as const,
              id: 'q1',
              qNumber: 35,
              text: "Which of the following is a primary benefit of using a state management library in a large-scale application?",
              description: "When building applications, managing state can become complex.",
              options: [
                "To reduce the number of components.",
                "To centralize and manage application state more predictably.",
                "To automatically style all components.",
                "To make direct API calls from any component.",
              ],
              correctAnswerIndices: [1],
              explanation: "Centralized state management libraries like Redux or BLoC provide a single source of truth, making state changes predictable and easier to debug."
            },
            {
              type: 'question' as const,
              id: 'q2',
              qNumber: 36,
              text: "What is the primary purpose of a 'key' prop in a list of React components?",
              description: "",
              options: [
                "To add styling to the component.",
                "To give React a stable identity for each element, helping it optimize rendering.",
                "To uniquely identify the component for API calls.",
                "To set the component's background color.",
              ],
              correctAnswerIndices: [1],
              explanation: "Keys help React identify which items have changed, are added, or are removed. Keys should be stable, predictable, and unique."
            }
          ]
        }
      ]
    },
    {
      type: 'question' as const,
      id: 'q3',
      qNumber: 37,
      text: 'This is a standalone question without a topic or case study.',
      description: '',
      options: ['A', 'B', 'C', 'D'],
      correctAnswerIndices: [0],
      explanation: 'This is the explanation for the standalone question.'
    }
  ],
};

// --- Helper function to get a flat list of questions ---
const getAllQuestions = (items) => {
  let questions = [];
  items.forEach(item => {
    if (item.type === 'question') {
      questions.push(item);
    } else if (item.type === 'topic' && item.items) {
      questions = [...questions, ...getAllQuestions(item.items)];
    } else if (item.type === 'case_study' && item.questions) {
      questions = [...questions, ...item.questions];
    }
  });
  return questions;
};

// --- Styled Components ---

const PageContainer = styled.div`
  display: flex;
  flex-direction: column;
  gap: 16px;
`;

const HeaderContainer = styled.div`
  display: flex;
  flex-direction: column;
  gap: 16px;
`;

const HeaderTopRow = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  gap: 16px;
  flex-wrap: wrap;
`;

const FileName = styled.h2`
  color: #2c2c54;
  font-weight: bold;
  font-size: 22px;
`;

const HeaderActions = styled.div`
  display: flex;
  align-items: center;
  gap: 12px;
`;

const SearchInput = styled.div`
  display: flex;
  align-items: center;
  background-color: white;
  border: 1px solid #E0E0E0;
  border-radius: 12px;
  padding: 0 12px;
  width: 250px;
  input {
    border: none;
    outline: none;
    padding: 12px 8px;
    font-size: 14px;
    flex-grow: 1;
  }
`;

const GoToFieldContainer = styled.div<{ $show: boolean; }>`
  display: flex;
  justify-content: center;
  align-items: center;
  gap: 8px;
  padding: ${({ $show }) => $show ? '8px 0' : '0'};
  overflow: hidden;
  transition: all 0.3s ease-in-out;
  max-height: ${({ $show }) => $show ? '60px' : '0'};
  opacity: ${({ $show }) => $show ? '1' : '0'};
`;

const ContentContainer = styled.div`
  background-color: white;
  border-radius: 12px;
  border: 1px solid #E0E0E0;
`;

const TopicHeader = styled.div`
  display: flex;
  align-items: center;
  gap: 16px;
  padding: 24px 16px 16px 16px;
  border-bottom: 1px solid #f0f0f0;
`;

const CaseStudyContainer = styled.div`
  background-color: rgba(239, 234, 254, 0.4);
  border-radius: 18px;
  border: 1px solid rgba(103, 58, 183, 0.2);
  padding: 26px;
  margin: 16px;
`;

const QuestionCardContainer = styled.div`
  padding: 26px;
  box-shadow: 0 5px 15px rgba(103, 58, 183, 0.04);
`;

const QuestionHeader = styled.div`
  display: flex;
  align-items: flex-start;
  gap: 18px;
  margin-bottom: 24px;
`;

const QuestionNumber = styled.div`
  background-color: rgba(103, 58, 183, 0.12);
  color: #673AB7;
  font-weight: bold;
  font-size: 16px;
  padding: 12px;
  border-radius: 10px;
`;

const QuestionText = styled.p`
  font-size: 17px;
  font-weight: 500;
  line-height: 1.34;
  flex: 1;
`;

const Label = styled.p`
  color: #BDBDBD;
  font-weight: 600;
  font-size: 12px;
  text-transform: uppercase;
  letter-spacing: 0.8px;
  margin-bottom: 8px;
`;

const OptionsList = styled.div`
  display: flex;
  flex-direction: column;
  gap: 6px;
  padding-left: 76px; // Aligns with question text
`;

const OptionItem = styled.div<{ $isCorrect: boolean; }>`
  border: 1.1px solid ${({ $isCorrect }) => $isCorrect ? '#38b000' : '#E0E0E0'};
  background-color: ${({ $isCorrect }) => $isCorrect ? '#F4FBF6' : 'transparent'};
  border-radius: 8px;
  padding: 11px 14px;
  font-size: 15px;
  line-height: 1.35;
`;

const ActionButtons = styled.div`
  display: flex;
  justify-content: flex-end;
  gap: 8px;
  margin-top: 26px;
`;

const ReportButton = styled.button`
  border: 1px solid rgba(255, 152, 0, 0.5);
  color: #FF9800;
  background: none;
  padding: 10px 12px;
  border-radius: 8px;
  cursor: pointer;
  display: flex;
  align-items: center;
  gap: 8px;
`;

const PrimaryButton = styled.button`
  border: none;
  background-color: #673AB7;
  color: white;
  padding: 10px 12px;
  border-radius: 8px;
  cursor: pointer;
  display: flex;
  align-items: center;
  gap: 8px;
`;

const ExplanationSection = styled.div`
  padding-top: 16px;
  margin-top: 16px;
  border-top: 1.1px solid #E0E0E0;
`;

const PaginationContainer = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 16px 24px;
  background-color: white;
  border: 1px solid #E0E0E0;
  border-radius: 12px;
`;

const PageButton = styled.button`
  background: none;
  border: none;
  cursor: pointer;
  &:disabled { cursor: not-allowed; svg { color: #BDBDBD; }}
`;

// --- The Main Page Component ---
const SimulationPage = () => {
  const [showGoTo, setShowGoTo] = useState(false);
  const [showAnswer, setShowAnswer] = useState(false);

  // In a real app, this would be managed by state and props.
  // For this UI build, we are just showing the first question.
  const allQuestions = getAllQuestions(mockData.items);
  const currentQuestion = allQuestions[0];
  const parentCaseStudy = mockData.items[0].items[0]; // Manually selecting for UI build
  const parentTopic = mockData.items[0]; // Manually selecting for UI build

  return (
    <PageContainer>
      <HeaderContainer>
        <HeaderTopRow>
          <FileName>{mockData.fileName}</FileName>
          {/* Header Actions can be added here if needed */}
        </HeaderTopRow>
        <GoToFieldContainer $show={showGoTo}>
          <input type="text" placeholder="Go to..." style={{ width: '150px', textAlign: 'center' }} />
          <PrimaryButton style={{ padding: '8px 16px' }}>Go</PrimaryButton>
        </GoToFieldContainer>
      </HeaderContainer>

      <ContentContainer>
        {/* We conditionally render the headers based on our sample data structure */}
        {parentTopic && <TopicHeader>...</TopicHeader>}
        {parentCaseStudy && <CaseStudyContainer>...</CaseStudyContainer>}

        {currentQuestion && (
          <QuestionCardContainer>
            <QuestionHeader>
              <QuestionNumber>Q: {currentQuestion.qNumber}</QuestionNumber>
              <QuestionText>{currentQuestion.text}</QuestionText>
            </QuestionHeader>
            <Label>Options</Label>
            <OptionsList>
              {currentQuestion.options.map((opt, index) => (
                <OptionItem key={index} $isCorrect={showAnswer && currentQuestion.correctAnswerIndices.includes(index)}>
                  {String.fromCharCode(65 + index)}. {opt}
                </OptionItem>
              ))}
            </OptionsList>
            <ActionButtons>
              <ReportButton>Report</ReportButton>
              <PrimaryButton onClick={() => setShowAnswer(!showAnswer)}>
                {showAnswer ? 'Hide Answer' : 'Show Answer'}
              </PrimaryButton>
            </ActionButtons>
            {showAnswer && (
              <ExplanationSection>
                <Label>Explanation</Label>
                <p>{currentQuestion.explanation}</p>
              </ExplanationSection>
            )}
          </QuestionCardContainer>
        )}
      </ContentContainer>

      <PaginationContainer>
        <span>Question {currentQuestion?.qNumber} of {mockData.totalQuestions}</span>
        <div style={{ display: 'flex', gap: '8px' }}>
          <PageButton>Back</PageButton>
          <PageButton>Next</PageButton>
        </div>
      </PaginationContainer>
    </PageContainer>
  );
};

export default SimulationPage;
