// src/components/common/Sidebar.tsx
import React from 'react';
import styled from 'styled-components';
import { NavLink } from 'react-router-dom';

// The main container, styled to match the Flutter Container widget
const SidebarContainer = styled.div`
  width: 255px;
  background-color: white;
  border-radius: 20px;
  box-shadow: 0 6px 18px rgba(0, 0, 0, 0.08);
  border: 1px solid #E0E0E0;
  padding: 8px 0; /* Vertical padding to space the list from the container edges */
  align-self: start; /* Aligns to the top of the container */
  position: sticky; /* Makes it stick to the top when scrolling */
  top: 24px; /* A little space from the header */
`;

// This now acts as the ListView
const NavList = styled.nav`
  display: flex;
  flex-direction: column;
`;

// Styles updated to match the InkWell and AnimatedContainer
const StyledNavLink = styled(NavLink)`
  color: rgba(0, 0, 0, 0.85);
  text-decoration: none;
  font-weight: 500;
  letter-spacing: 0.14px;
  padding: 13px 22px;
  margin: 0;
  border-bottom: 1px solid #f5f5f5; /* Separator line */
  transition: all 0.22s ease-in-out;

  /* Styles for the ACTIVE link */
  &.active {
    background-color: rgba(44, 44, 84, 0.09); /* themeBlue.withOpacity(0.09) */
    color: #2c2c54; /* themeBlue */
    font-weight: 600;
    margin: 3px 6px;
    border: 1.3px solid rgba(44, 44, 84, 0.21);
    border-radius: 13px;
    /* The active link doesn't need a bottom border separator */
    border-bottom: none; 
  }
`;

// The LogoutButton is styled to look just like an inactive NavLink
const LogoutButton = styled.button`
  /* Base styles to match NavLink */
  color: rgba(0, 0, 0, 0.85);
  font-weight: 500;
  letter-spacing: 0.14px;
  padding: 13px 22px;
  font-size: 16px; /* Explicitly set font size */
  font-family: inherit;

  /* Resetting button defaults */
  background: none;
  border: none;
  border-bottom: 1px solid #f5f5f5;
  cursor: pointer;
  text-align: left;
  width: 100%;
`;

// The component itself remains the same logically
const Sidebar = () => {
  return (
    <SidebarContainer>
      <NavList>
        <StyledNavLink to="/" end>Dashboard</StyledNavLink>
        <StyledNavLink to="/orders">Orders</StyledNavLink>
        <StyledNavLink to="/downloads">Downloads</StyledNavLink>
        <StyledNavLink to="/addresses">Addresses</StyledNavLink>
        <StyledNavLink to="/account-details">Account Details</StyledNavLink>
        <LogoutButton onClick={() => alert('Logout clicked!')}>Log out</LogoutButton>
      </NavList>
    </SidebarContainer>
  );
};

export default Sidebar;
