// src/pages/DownloadsPage.tsx
import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { Link } from 'react-router-dom';
import { useUser } from '../context/UserContext';
import { supabase } from '../supabaseClient'; // Assuming supabase is used, as in other files

// --- Helper for UI ---
const formatDate = (dateString: string | null) => {
  if (!dateString) return 'Never';
  // Handles 'YYYY-MM-DD HH:MM:SS' format
  const date = new Date(dateString.replace(' ', 'T'));
  return date.toLocaleDateString('en-US', {
    year: 'numeric',
    month: 'long',
    day: 'numeric',
  });
};

// --- Styled Components (UI/UX Updated) ---

const PageHeader = styled.h2`
  font-size: 28px;
  font-weight: 600;
  color: ${({ theme }) => theme.colors.textPrimary};
  margin-bottom: 24px;
`;

const DownloadsContainer = styled.div`
  background-color: ${({ theme }) => theme.colors.surface};
  border-radius: ${({ theme }) => theme.borderRadius.large};
  border: 1px solid ${({ theme }) => theme.colors.lightGrey};
  box-shadow: ${({ theme }) => theme.shadows.card};
  overflow: hidden;
`;

const TableWrapper = styled.div`
  /* This allows the table to scroll horizontally on small screens */
  overflow-x: auto;
`;

const DownloadsTable = styled.div`
  display: table;
  width: 100%;
  min-width: 600px; /* Prevents squishing on small screens */
  border-collapse: collapse;
`;

const Row = styled.div`
  display: table-row;
  transition: background-color 0.2s ease-in-out;

  &:not(.header):hover {
    background-color: ${({ theme }) => theme.colors.background};
  }
`;

const HeaderRow = styled(Row)`
  background-color: rgba(45, 22, 132, 0.08);
  color: ${({ theme }) => theme.colors.primary};
  font-weight: 700;
  font-size: 14px;
  text-transform: uppercase;
  letter-spacing: 0.5px;
`;

const Cell = styled.div`
  display: table-cell;
  padding: 20px 24px;
  vertical-align: middle;
  border-bottom: 1px solid ${({ theme }) => theme.colors.lightGrey};
  color: ${({ theme }) => theme.colors.textSecondary};

  &.product-name {
    font-weight: 600;
    color: ${({ theme }) => theme.colors.textPrimary};
  }
`;

const ButtonContainer = styled.div`
  display: flex;
  gap: 12px;
  justify-content: flex-start;
`;

const PrimaryButton = styled.a`
  display: inline-block;
  background-color: ${({ theme }) => theme.colors.primary};
  color: ${({ theme }) => theme.colors.textOnPrimary};
  padding: 8px 18px;
  border-radius: ${({ theme }) => theme.borderRadius.medium};
  text-decoration: none;
  font-weight: 600;
  font-size: 14px;
  text-align: center;
  transition: opacity 0.2s ease-in-out;

  &:hover {
    opacity: 0.9;
  }
`;

const SecondaryButton = styled(Link)`
  display: inline-block;
  background-color: transparent;
  color: ${({ theme }) => theme.colors.primary};
  border: 1px solid ${({ theme }) => theme.colors.primary};
  padding: 8px 18px;
  border-radius: ${({ theme }) => theme.borderRadius.medium};
  text-decoration: none;
  font-weight: 600;
  font-size: 14px;
  transition: background-color 0.2s ease-in-out, color 0.2s ease-in-out;

  &:hover {
    background-color: ${({ theme }) => theme.colors.button};
  }
`;

const MessageContainer = styled.div`
  padding: 40px;
  text-align: center;
  color: ${({ theme }) => theme.colors.textSecondary};
  background-color: ${({ theme }) => theme.colors.surface};
  border-radius: ${({ theme }) => theme.borderRadius.large};
  border: 1px solid ${({ theme }) => theme.colors.lightGrey};
  box-shadow: ${({ theme }) => theme.shadows.card};
`;

const NoDownloadsMessage = styled.div`
  padding: 48px 24px;
  text-align: center;
  color: ${({ theme }) => theme.colors.textSecondary};
`;


// --- The Page Component (Business Logic Untouched) ---
const DownloadsPage = () => {
  const { user, isLoading: isUserLoading } = useUser();
  const [downloads, setDownloads] = useState<any[]>([]);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    if (!user || isUserLoading) {
      if(!isUserLoading) setIsLoading(false);
      return;
    }

    const getPurchasedDownloads = async () => {
      setIsLoading(true);
      try {
        const { data: apiResponse, error } = await supabase.functions.invoke('get-wc-data', {
          body: {
            method: 'GET',
            endpoint: 'cwc/downloads', 
            user_id: user.id 
          },
        });

        if (error) throw error;

        if (apiResponse && apiResponse.Success && Array.isArray(apiResponse.Data)) {
          setDownloads(apiResponse.Data);
        } else {
          setDownloads([]);
        }
      } catch (error) {
        console.error("Error fetching downloads:", error);
        setDownloads([]);
      } finally {
        setIsLoading(false);
      }
    };

    getPurchasedDownloads();
  }, [user, isUserLoading]);

  if (isLoading) {
    return <MessageContainer>Loading your downloads...</MessageContainer>;
  }

  if (!user) {
    return <MessageContainer>Please log in to view your downloads.</MessageContainer>;
  }

  return (
    <div>
      <PageHeader>Downloads</PageHeader>
      <DownloadsContainer>
        {downloads.length > 0 ? (
          <TableWrapper>
            <DownloadsTable>
              <HeaderRow className="header">
                <Cell>Product</Cell>
                <Cell>Downloads Remaining</Cell>
                <Cell>Expires</Cell>
                <Cell>Actions</Cell>
              </HeaderRow>
              {downloads.map((item: any) => (
                <Row key={item.download_id}>
                  <Cell className="product-name">{item.product_name}</Cell>
                  <Cell>{item.downloads_remaining}</Cell>
                  <Cell>{formatDate(item.access_expires)}</Cell>
                  <Cell>
                    <ButtonContainer>
                      <PrimaryButton href={item.download_url} target="_blank" download>
                        Download
                      </PrimaryButton>
                      {item.supabase_file_id && (
                        <SecondaryButton to={`/practice/${item.supabase_file_id}`}>
                          Practice
                        </SecondaryButton>
                      )}
                    </ButtonContainer>
                  </Cell>
                </Row>
              ))}
            </DownloadsTable>
          </TableWrapper>
        ) : (
          <NoDownloadsMessage>You have no available downloads.</NoDownloadsMessage>
        )}
      </DownloadsContainer>
    </div>
  );
};

export default DownloadsPage;
