// src/components/common/Sidebar.tsx
import React from 'react';
import styled from 'styled-components';
import { NavLink } from 'react-router-dom';

// Props for mobile state and logout
interface SidebarProps {
  isOpen: boolean;
  onLogoutClick: () => void;
}

// Container matches Flutter's white rounded card with shadow/border
const SidebarContainer = styled.aside<{ isOpen: boolean }>`
  width: 255px;
  background-color: ${({ theme }) => theme.colors.surface};
  border-radius: ${({ theme }) => theme.borderRadius.large};
  box-shadow: 0 6px 18px rgba(0, 0, 0, 0.08);
  border: 1.2px solid ${({ theme }) => theme.colors.lightGrey};
  overflow-y: auto; // Scrollable like Flutter's SingleChildScrollView
  max-height: calc(100vh - 48px); // Fit screen with margin
  margin: 24px;
  align-self: flex-start;
  position: sticky;
  top: 24px;

  @media (max-width: 768px) {
    position: fixed;
    left: 0;
    top: 0;
    height: 100%;
    width: 255px; // Keep width for mobile too
    margin: 0;
    border-radius: 0;
    transform: ${({ isOpen }) => isOpen ? 'translateX(0)' : 'translateX(-100%)'};
    transition: transform 0.3s ease-in-out;
    z-index: 1000;
    box-shadow: none;
    border: none;
  }
`;

// Nav list with no last divider
const NavList = styled.nav`
  display: flex;
  flex-direction: column;

  > *:last-child {
    border-bottom: none;
  }
`;

// Shared styles for links and button
const navItemStyles = `
  display: block;
  text-decoration: none;
  font-size: 15px; // Matches Flutter titleMedium
  transition: all 0.22s ease-in-out;
  border-bottom: 1px solid #f0f0f0; // Divider
  padding: 13px 22px; // Exact Flutter padding
  letter-spacing: 0.14px; // Flutter letter spacing
  color: rgba(0, 0, 0, 0.85); // Black opacity 0.85
  font-weight: 500;
  border-radius: 13px;
  margin: 0;

  &:hover {
    background-color: rgba(0, 0, 0, 0.02); // Subtle hover feedback
  }
`;

const StyledNavLink = styled(NavLink)`
  ${navItemStyles}

  &.active {
    color: ${({ theme }) => theme.colors.primary}; // themeBlue
    background-color: rgba(45, 22, 132, 0.09); // themeBlue opacity 0.09
    font-weight: 600;
    margin: 3px 6px; // Inset margin
    border: 1.3px solid rgba(45, 22, 132, 0.21); // Border matching opacity/width
  }

  @media (max-width: 768px) {
    &.active {
      margin: 0;
      border: none;
    }
  }
`;

const LogoutButton = styled.button`
  ${navItemStyles}
  background: none;
  cursor: pointer;
  text-align: left;
  width: 100%;
`;

const Sidebar: React.FC<SidebarProps> = ({ isOpen, onLogoutClick }) => {
  return (
    <SidebarContainer isOpen={isOpen}>
      <NavList>
        <StyledNavLink to="/" end>Dashboard</StyledNavLink>
        <StyledNavLink to="/orders">Orders</StyledNavLink>
        <StyledNavLink to="/downloads">Downloads</StyledNavLink>
        <StyledNavLink to="/addresses">Addresses</StyledNavLink>
        <StyledNavLink to="/account-details">Account details</StyledNavLink>
        <LogoutButton onClick={onLogoutClick}>Log out</LogoutButton>
      </NavList>
    </SidebarContainer>
  );
};

export default Sidebar;
