// src/pages/OrdersPage.tsx
import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { Link } from 'react-router-dom';
import { supabase } from '../supabaseClient';
import { useUser } from '../context/UserContext';

// This type defines the shape of an order coming from your custom WC API
type Order = {
  id: number;
  date_created: string;
  status: string;
  total: string;
  currency: string;
};

// --- Styled Components (UI/UX Updated) ---

const PageHeader = styled.h2`
  font-size: 28px;
  font-weight: 600;
  color: ${({ theme }) => theme.colors.textPrimary};
  margin-bottom: 24px;
`;

const OrdersListContainer = styled.div`
  background-color: ${({ theme }) => theme.colors.surface};
  border-radius: ${({ theme }) => theme.borderRadius.large};
  border: 1px solid ${({ theme }) => theme.colors.lightGrey};
  box-shadow: ${({ theme }) => theme.shadows.card};
  overflow: hidden; /* Ensures the child border-radius is clipped correctly */
`;

const OrderRow = styled.div`
  display: grid;
  /* Adjusted grid columns for better spacing */
  grid-template-columns: 1fr 1.5fr 1.5fr 1.5fr 1fr;
  align-items: center;
  padding: 16px 24px;
  border-bottom: 1px solid ${({ theme }) => theme.colors.lightGrey};
  transition: background-color 0.2s ease-in-out;

  &:last-child {
    border-bottom: none;
  }

  &:hover {
    background-color: ${({ theme }) => theme.colors.background};
  }
`;

const HeaderRow = styled(OrderRow)`
  background-color: ${({ theme }) => theme.colors.button};
  color: ${({ theme }) => theme.colors.primary};
  font-weight: 600;
  font-size: 14px;
  text-transform: uppercase;
  letter-spacing: 0.5px;

  &:hover {
    background-color: ${({ theme }) => theme.colors.button};
  }
`;

const DataCell = styled.div`
  color: ${({ theme }) => theme.colors.textSecondary};
  font-size: 15px;

  &.order-id {
    color: ${({ theme }) => theme.colors.primary};
    font-weight: 600;
    text-decoration: none;
  }
`;

const getStatusColors = (status: string, theme: any) => {
  switch (status) {
    case 'completed':
      return { bg: '#E8F5E9', text: '#2E7D32' };
    case 'processing':
      return { bg: theme.colors.button, text: theme.colors.primary };
    case 'pending':
      return { bg: '#FFF3E0', text: '#E65100' };
    default:
      return { bg: theme.colors.lightGrey, text: theme.colors.textSecondary };
  }
};

const StatusBadge = styled.span<{ status: string }>`
  background-color: ${({ status, theme }) => getStatusColors(status, theme).bg};
  color: ${({ status, theme }) => getStatusColors(status, theme).text};
  padding: 6px 12px;
  border-radius: 999px; /* Pill shape */
  font-weight: 600;
  font-size: 12px;
  text-transform: capitalize;
  display: inline-block;
`;

const ViewButton = styled(Link)`
  background-color: ${({ theme }) => theme.colors.primary};
  color: ${({ theme }) => theme.colors.textOnPrimary};
  padding: 8px 18px;
  border-radius: ${({ theme }) => theme.borderRadius.medium};
  text-decoration: none;
  font-weight: 600;
  text-align: center;
  justify-self: end; /* Aligns button to the right end of the cell */
  transition: opacity 0.2s ease-in-out;

  &:hover {
    opacity: 0.9;
  }
`;

const MessageContainer = styled.div`
  padding: 40px;
  text-align: center;
  color: ${({ theme }) => theme.colors.textSecondary};
  background-color: ${({ theme }) => theme.colors.surface};
  border-radius: ${({ theme }) => theme.borderRadius.large};
  border: 1px solid ${({ theme }) => theme.colors.lightGrey};
  box-shadow: ${({ theme }) => theme.shadows.card};
`;

const NoOrdersMessage = styled.div`
  padding: 48px 24px;
  text-align: center;
  color: ${({ theme }) => theme.colors.textSecondary};
`;


// --- The Main Page Component (Business Logic Untouched) ---
const OrdersPage = () => {
  const { user, isLoading: isUserLoading } = useUser();
  const [orders, setOrders] = useState<Order[]>([]);
  const [isPageLoading, setPageLoading] = useState(true);

  useEffect(() => {
    const getOrdersFromWP = async () => {
      if (!user || !user.id) {
        setPageLoading(false);
        return;
      }

     try {
        const payload = { 
          method: 'GET',
          endpoint: 'cwc/orders',
          user_id: user.id
        };
        console.log('Sending to Supabase:', payload);

        const { data: apiResponse, error } = await supabase.functions.invoke('get-wc-data', {
          body: payload,
        });

        if (error) throw error;

        if (apiResponse && apiResponse.Success && Array.isArray(apiResponse.Data)) {
            setOrders(apiResponse.Data);
        }

      } catch (error) {
        console.error('Error fetching orders from WordPress:', error);
      } finally {
        setPageLoading(false);
      }
    };

    if (!isUserLoading) {
      getOrdersFromWP();
    }
  }, [isUserLoading, user]);

  if (isUserLoading || isPageLoading) return <MessageContainer>Loading orders...</MessageContainer>;
  if (!user) return <MessageContainer>Please log in to view your orders.</MessageContainer>;

  return (
    <div>
      <PageHeader>Orders</PageHeader>
      <OrdersListContainer>
        <HeaderRow>
          <DataCell>Order</DataCell>
          <DataCell>Date</DataCell>
          <DataCell>Status</DataCell>
          <DataCell>Total</DataCell>
          <DataCell style={{ textAlign: 'right' }}>Actions</DataCell>
        </HeaderRow>

        {orders.length > 0 ? orders.map((order) => (
          <OrderRow key={order.id}>
            <DataCell className="order-id">#{order.id}</DataCell>
            <DataCell>{new Date(order.date_created).toLocaleDateString()}</DataCell>
            <DataCell><StatusBadge status={order.status}>{order.status}</StatusBadge></DataCell>
            <DataCell>{`${order.currency}${order.total}`}</DataCell>
            <DataCell style={{ display: 'flex', justifyContent: 'flex-end' }}>
              <ViewButton to={`/orders/${order.id}`}>View</ViewButton>
            </DataCell>
          </OrderRow>
        )) : (
            <NoOrdersMessage>You have no orders yet.</NoOrdersMessage>
        )}
      </OrdersListContainer>
    </div>
  );
};

export default OrdersPage;
