// src/components/common/Sidebar.tsx
import React from 'react';
import styled from 'styled-components';
import { NavLink } from 'react-router-dom';

const SidebarContainer = styled.div`
  width: 260px;
  background-color: ${({ theme }) => theme.colors.surface};
  border-right: 1px solid #e9e9e9;
  flex-shrink: 0; 
  padding: 16px;
  display: flex; /* Added for alignment */
  flex-direction: column; /* Added for alignment */
`;

const NavList = styled.nav`
  display: flex;
  flex-direction: column;
  gap: 8px; 
`;

const StyledNavLink = styled(NavLink)`
  padding: 14px 20px;
  color: ${({ theme }) => theme.colors.textSecondary};
  text-decoration: none;
  font-weight: 500;
  transition: all 0.2s ease-in-out;
  border-radius: 8px;

  &.active {
    background-color: #EFEAFE;
    color: #673AB7;
  }

  &:not(.active):hover {
    background-color: #f5f5f5;
  }
`;

const LogoutButton = styled.button`
  width: 100%;
  padding: 14px 20px;
  color: ${({ theme }) => theme.colors.textSecondary};
  font-size: 16px;
  font-weight: 500;
  font-family: inherit;
  background: none;
  border: none;
  border-radius: 8px;
  cursor: pointer;
  text-align: left;
  margin-top: 8px; 

  &:hover {
    background-color: #f5f5f5; 
  }
`;

const Sidebar = () => {
  return (
    <SidebarContainer>
      <NavList>
        <StyledNavLink to="/" end>Dashboard</StyledNavLink>
        <StyledNavLink to="/orders">Orders</StyledNavLink>
        <StyledNavLink to="/downloads">Downloads</StyledNavLink>
        <StyledNavLink to="/addresses">Addresses</StyledNavLink>
        <StyledNavLink to="/account-details">Account Details</StyledNavLink>
        <LogoutButton onClick={() => alert('Logout clicked!')}>Log out</LogoutButton>
      </NavList>
    </SidebarContainer>
  );
};

export default Sidebar;
