// src/components/common/MainLayout.tsx
import React, { useState } from 'react';
import styled from 'styled-components';
import Sidebar from './Sidebar';
import Header from './Header';
import Footer from './Footer';
import { useUser } from '../../context/UserContext';

const AppShell = styled.div`
  display: flex;
  flex-direction: column;
  height: 100%;
  background-color: ${({ theme }) => theme.colors.background};
`;

const MainWrapper = styled.div`
  display: flex;
  flex-grow: 1;
  overflow: hidden;

  /* On larger screens, the sidebar is visible, so we need space */
  @media (min-width: 769px) {
    padding-right: 24px;
  }
`;

const PageContentWrapper = styled.div`
  flex-grow: 1;
  overflow-y: auto; /* Allows only the content area to scroll */
  display: flex;
  flex-direction: column;
`;

const ContentContainer = styled.main`
  flex-grow: 1; 
  /* Responsive padding for the main content area */
  padding: 24px 32px;

  @media (max-width: 950px) {
    padding: 24px;
  }

  @media (max-width: 650px) {
    padding: 16px;
  }
`;

const Overlay = styled.div<{ isOpen: boolean; }>`
  display: none; /* Hidden by default */

  /* Show overlay only on mobile/tablet when menu is open */
  @media (max-width: 768px) {
    display: ${({ isOpen }) => (isOpen ? 'block' : 'none')};
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background-color: rgba(0,0,0,0.4);
    z-index: 999; /* Below the sidebar but above the content */
  }
`;

interface MainLayoutProps {
  children: React.ReactNode;
}

const MainLayout: React.FC<MainLayoutProps> = ({ children }) => {
  const [isMenuOpen, setMenuOpen] = useState(false);

  const handleMenuToggle = () => {
    setMenuOpen(!isMenuOpen);
  };

  // A function to close the menu, useful for the overlay
  const handleCloseMenu = () => {
    setMenuOpen(false);
  }

  return (
    <AppShell>
      <Header onMenuClick={handleMenuToggle} />
      <MainWrapper>
        <Sidebar isOpen={isMenuOpen} />
        <Overlay isOpen={isMenuOpen} onClick={handleCloseMenu} />
        <PageContentWrapper>
          <ContentContainer>
            {children}
          </ContentContainer>
          <Footer />
        </PageContentWrapper>
      </MainWrapper>
    </AppShell>
  );
};

export default MainLayout;
