// src/components/common/Sidebar.tsx
import React from 'react';
import styled from 'styled-components';
import { NavLink } from 'react-router-dom';

interface SidebarProps {
  isOpen: boolean;
}

// Styles updated to be responsive
const SidebarContainer = styled.aside<{ isOpen: boolean; }>`
  width: 260px; /* A bit wider to match the look */
  background-color: white;
  padding: 16px 0; /* Vertical padding */
  flex-shrink: 0;

  @media (max-width: 650px) {
    position: fixed;
    left: 0;
    top: 0;
    height: 100%;
    transform: ${({ isOpen }) => isOpen ? 'translateX(0)' : 'translateX(-100%)'};
    transition: transform 0.3s ease-in-out;
    z-index: 1000;
    box-shadow: 0 0 15px rgba(0,0,0,0.2);
    border-right: none;
  }
`;

const NavList = styled.nav`
  display: flex;
  flex-direction: column;
`;

// Styles updated to exactly match the Flutter code
const StyledNavLink = styled(NavLink)`
  color: rgba(0, 0, 0, 0.85);
  text-decoration: none;
  font-weight: 500;
  letter-spacing: 0.14px;
  padding: 13px 22px;
  margin: 0;
  border-bottom: 1px solid #f5f5f5;
  transition: all 0.22s ease-in-out;

  &.active {
    background-color: rgba(44, 44, 84, 0.09); /* themeBlue.withOpacity(0.09) */
    color: #2c2c54;
    font-weight: 600;
    margin: 3px 6px;
    border: 1.3px solid rgba(44, 44, 84, 0.21);
    border-radius: 13px;
    border-bottom: none;
  }
`;

const LogoutButton = styled.button`
  /* Copied styles from NavLink for consistency */
  color: rgba(0, 0, 0, 0.85);
  font-weight: 500;
  letter-spacing: 0.14px;
  padding: 13px 22px;
  margin: 0;
  font-size: 16px;
  font-family: inherit;
  background: none;
  border: none;
  border-bottom: 1px solid #f5f5f5;
  cursor: pointer;
  text-align: left;
  width: 100%;
`;

const Sidebar: React.FC<SidebarProps> = ({ isOpen }) => {
  return (
    <SidebarContainer isOpen={isOpen}>
      <NavList>
        <StyledNavLink to="/" end>Dashboard</StyledNavLink>
        <StyledNavLink to="/orders">Orders</StyledNavLink>
        <StyledNavLink to="/downloads">Downloads</StyledNavLink>
        <StyledNavLink to="/addresses">Addresses</StyledNavLink>
        <StyledNavLink to="/account-details">Account Details</StyledNavLink>
        <LogoutButton onClick={() => alert('Logout clicked!')}>Log out</LogoutButton>
      </NavList>
    </SidebarContainer>
  );
};

export default Sidebar;
