// src/pages/SimulationPage.tsx
import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { useParams } from 'react-router-dom';
import { supabase } from '../supabaseClient';
import HtmlRenderer from '../components/utils/HtmlRenderer';

// --- Type Definitions ---
type Question = { answer: string[]; options: string[]; question: string; explanation: string; question_number: string; };
type Topic = { questions: Question[]; case_study?: string; topic_name?: string; };
type SimulationData = { fileName: string; topics: Record<string, Topic>; };
type SupabaseFile = { 'ai-parsed-data': { result: { topics: Record<string, Topic>; }; }; filename: string; };
type StructuredItem = { type: 'question'; content: Question; parent?: { topic?: string; case_study?: string } };

// --- Helper function ---
const getAllQuestions = (topics: Record<string, Topic>): Question[] => {
  if (!topics) return [];
  let questions: Question[] = [];
  Object.values(topics).forEach((topic: Topic) => {
    if (topic.questions && Array.isArray(topic.questions)) {
      questions.push(...topic.questions);
    }
  });
  return questions.map((q, index) => ({ ...q, question_number: q.question_number || (index + 1).toString() }));
};

// --- Styled Components (Updated for new UI) ---
const PageContainer = styled.div` display: flex; flex-direction: column; gap: 24px; `;
const HeaderContainer = styled.div` display: flex; justify-content: space-between; align-items: center; flex-wrap: wrap; gap: 16px; `;
const FileName = styled.h2` color: #333; font-weight: 600; font-size: 24px; `;

const ContentContainer = styled.div`
  background-color: white;
  border-radius: 12px;
  border: 1px solid #EAEAEA;
`;

const CaseStudyContainer = styled.div`
  padding: 24px 32px;
  border-bottom: 1px solid #EAEAEA;
  h3 {
    font-size: 18px;
    font-weight: 600;
    margin-bottom: 16px;
  }
`;

const QuestionCardContainer = styled.div`
  padding: 24px 32px;
`;

const QuestionHeader = styled.div`
  display: flex;
  align-items: flex-start;
  gap: 18px;
  margin-bottom: 24px;
`;

const QuestionNumber = styled.div`
  background-color: #EFEAFE; /* Light purple from screenshot */
  color: #673AB7;
  font-weight: bold;
  font-size: 14px;
  padding: 10px;
  border-radius: 8px;
  line-height: 1;
`;

const Label = styled.p`
  color: #A0A0A0;
  font-weight: 600;
  font-size: 12px;
  text-transform: uppercase;
  letter-spacing: 0.8px;
  margin-bottom: 16px;
  padding-left: 58px;
  @media(max-width: 600px) { padding-left: 0; }
`;

const OptionsList = styled.div`
  display: flex;
  flex-direction: column;
  gap: 12px; /* Increased gap between options */
  padding-left: 58px; /* Indent to align with question text */
  @media(max-width: 600px) { padding-left: 0; }
`;

const OptionItem = styled.div`
  border: 1.5px solid #EAEAEA;
  background-color: transparent;
  border-radius: 12px;
  padding: 16px;
  font-size: 15px;
  line-height: 1.5;
  display: flex;
  gap: 8px;
`;

const ActionButtons = styled.div`
  display: flex;
  justify-content: flex-end;
  gap: 12px;
  margin-top: 32px;
`;

const PrimaryButton = styled.button`
  border: none;
  background-color: #673AB7; /* themePurple */
  color: white;
  padding: 12px 20px;
  border-radius: 8px;
  cursor: pointer;
  font-weight: 600;
  font-size: 14px;
`;

const ExplanationSection = styled.div`
  padding-top: 24px;
  margin-top: 24px;
  border-top: 1.1px solid #EAEAEA;
`;

const PaginationContainer = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 16px;
  background-color: white;
  border: 1px solid #EAEAEA;
  border-radius: 12px;
`;

const PageButton = styled.button`
  background: none;
  border: 1px solid #EAEAEA;
  color: #333;
  padding: 8px 16px;
  border-radius: 8px;
  cursor: pointer;
  font-weight: 500;
  &:disabled { cursor: not-allowed; opacity: 0.5; }
`;

const LoadingContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 300px;
  font-size: 18px;
  color: #757575;
`;

// --- The Main Page Component ---
const SimulationPage = () => {
  const { fileId } = useParams<{ fileId: string }>();
  const [simulationData, setSimulationData] = useState<SimulationData | null>(null);
  const [structuredItems, setStructuredItems] = useState<StructuredItem[]>([]);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [isLoading, setLoading] = useState(true);
  const [showAnswer, setShowAnswer] = useState(false);

  useEffect(() => {
    if (!fileId) return;
    async function getSimulationData() {
      setLoading(true);
      const { data, error } = await supabase.from('files').select('filename, ai-parsed-data').eq('id', fileId).single<SupabaseFile>();
      if (error) console.error("Error fetching file:", error);
      else if (data?.['ai-parsed-data']?.result?.topics) {
        const topics = data['ai-parsed-data'].result.topics;
        setSimulationData({ fileName: data.filename, topics });
        const newStructuredItems: StructuredItem[] = [];
        Object.values(topics).forEach((topic: Topic) => {
          (topic.questions || []).forEach((q: Question) => {
            newStructuredItems.push({
              type: 'question',
              content: { ...q },
              parent: { topic: topic.topic_name, case_study: topic.case_study }
            });
          });
        });
        setStructuredItems(newStructuredItems);
      }
      setLoading(false);
    }
    getSimulationData();
  }, [fileId]);

  useEffect(() => {
    setShowAnswer(false);
  }, [currentIndex]);

  const handleNext = () => setCurrentIndex(prev => Math.min(prev + 1, structuredItems.length - 1));
  const handlePrev = () => setCurrentIndex(prev => Math.max(prev - 1, 0));

  const currentItem = structuredItems[currentIndex];

  if (isLoading) return <LoadingContainer>Loading Simulation...</LoadingContainer>;
  if (!currentItem) return <LoadingContainer>Could not load questions for this file.</LoadingContainer>;

  return (
    <PageContainer>
      <HeaderContainer>
        <FileName>{simulationData?.fileName}</FileName>
        {/* The full header will be a separate component later */}
      </HeaderContainer>

      <ContentContainer>
        {currentItem.parent?.case_study && 
          <CaseStudyContainer>
            <h3>Overview</h3>
            <HtmlRenderer htmlString={currentItem.parent.case_study} />
          </CaseStudyContainer>
        }

        <QuestionCardContainer>
          <QuestionHeader>
            <QuestionNumber>Q: {currentItem.content.question_number}</QuestionNumber>
            <HtmlRenderer htmlString={currentItem.content.question} />
          </QuestionHeader>
          <Label>OPTIONS</Label>
          <OptionsList>
            {currentItem.content.options.map((opt: string, index: number) => {
              const optionLetter = String.fromCharCode(65 + index);
              return (
                <OptionItem key={index}>
                   <b>{optionLetter}.</b> 
                   <HtmlRenderer htmlString={opt.replace(/^[A-Z][\.\)]\s*/, '')} />
                </OptionItem>
              );
            })}
          </OptionsList>
          <ActionButtons>
            <PrimaryButton onClick={() => setShowAnswer(!showAnswer)}>
              {showAnswer ? 'Hide Answer' : 'Show Answer'}
            </PrimaryButton>
          </ActionButtons>
          {showAnswer && (
            <ExplanationSection>
              <Label>Explanation</Label>
              <HtmlRenderer htmlString={currentItem.content.explanation} />
            </ExplanationSection>
          )}
        </QuestionCardContainer>
      </ContentContainer>

      <PaginationContainer>
        <span>Question {currentItem.content.question_number} of {structuredItems.length}</span>
        <div style={{ display: 'flex', gap: '8px' }}>
          <PageButton onClick={handlePrev} disabled={currentIndex === 0}>Back</PageButton>
          <PageButton onClick={handleNext} disabled={currentIndex === structuredItems.length - 1}>Next</PageButton>
        </div>
      </PaginationContainer>
    </PageContainer>
  );
};

export default SimulationPage;
