// src/components/common/MainLayout.tsx
import React from 'react';
import styled from 'styled-components';
import Sidebar from './Sidebar';
import Header from './Header';
import Footer from './Footer';

// The main container for the entire application view
const AppShell = styled.div`
  display: flex;
  flex-direction: column;
  height: 100%;
`;

// This wrapper holds the content BELOW the header
const MainWrapper = styled.div`
  display: flex;
  flex-grow: 1; /* This makes this section take up all space below the header */
  overflow: hidden; /* Prevents double scrollbars */
`;

// This wrapper holds the main page content and footer
const PageContentWrapper = styled.div`
  flex-grow: 1;
  overflow-y: auto; /* The scrollbar is now on this container */
  display: flex;
  flex-direction: column;
`;

const ContentContainer = styled.main`
  flex-grow: 1; 
  padding: 24px 32px;
  background-color: #f8f9fd; /* The corrected background color */
`;

interface MainLayoutProps {
  children: React.ReactNode;
}

const MainLayout: React.FC<MainLayoutProps> = ({ children }) => {
  return (
    <AppShell>
      <Header />
      <MainWrapper>
        <Sidebar />
        <PageContentWrapper>
          <ContentContainer>
            {children}
          </ContentContainer>
          <Footer />
        </PageContentWrapper>
      </MainWrapper>
    </AppShell>
  );
};

export default MainLayout;
