// src/pages/DashboardPage.tsx
import React, { useEffect, useState } from 'react';
import styled from 'styled-components';
import { useUser } from '../context/UserContext';
import { supabase } from '../supabaseClient';
import { Link } from 'react-router-dom';

// -- ICONS: Replace with your favorite library if needed
const IconMedal = ({ color = '#FFC107' }) => <svg width="22" height="22" viewBox="0 0 24 24" fill={color}><path d="M12 2C6.48 2 2 6.48 2 12s4.48 10 10 10 10-4.48 10-10S17.52 2 12 2zm0 18c-4.41 0-8-3.59-8-8s3.59-8 8-8 8 3.59 8 8-3.59 8-8 8zm-1-13h2v6h-2zm0 8h2v2h-2z" /></svg>;
const IconBag = ({ color = '#2196F3' }) => <svg width="22" height="22" viewBox="0 0 24 24" fill={color}><path d="M18 6h-2c0-2.21-1.79-4-4-4S8 3.79 8 6H6c-1.1 0-2 .9-2 2v12c0 1.1.9 2 2 2h12c1.1 0 2-.9 2-2V8c0-1.1-.9-2-2-2zm-6-2c1.1 0 2 .9 2 2h-4c0-1.1.9-2 2-2zm6 16H6V8h2v2h8V8h2v12z" /></svg>;
const IconCheckCircle = ({ color = '#42b983' }) => <svg width="22" height="22" viewBox="0 0 24 24" fill={color}><path d="M12 2C6.48 2 2 6.48 2 12s4.48 10 10 10 10-4.48 10-10S17.52 2 12 2zm-2 15l-5-5 1.41-1.41L10 14.17l7.59-7.59L19 8l-9 9z" /></svg>;
const IconEnvelope = ({ color = '#6366f1' }) => <svg width="22" height="22" viewBox="0 0 24 24" fill={color}><path d="M20 4H4c-1.1 0-1.99.9-1.99 2L2 18c0 1.1.9 2 2 2h16c1.1 0 2-.9 2-2V6c0-1.1-.9-2-2-2zm0 4l-8 5-8-5V6l8 5 8-5v2z" /></svg>;

// -- Styled components
const Page = styled.div`
  padding: 24px 0 0 0;
`;
const Welcome = styled.h2`
  font-size: 2rem;
  font-weight: 700;
  margin-bottom: 14px;
  color: #1a1a2c;
`;
const DashboardLayout = styled.div`
  display: grid;
  grid-template-columns: 2.3fr 1fr;
  gap: 32px;
  align-items: flex-start;
  @media (max-width: 950px) {
    grid-template-columns: 1fr;
    gap: 24px;
  }
`;
const MainCol = styled.div`
  display: flex;
  flex-direction: column;
  gap: 30px;
`;
const Card = styled.div`
  background: #fff;
  border-radius: 18px;
  box-shadow: 0 4px 20px #b0abf525;
  padding: 28px 36px;
  margin-bottom: 0;
