export default function DashboardPage() {
  const { user } = useUser();
  const [firstName, setFirstName] = useState('');
  const [rewards, setRewards] = useState(0);
  const [downloads, setDownloads] = useState<any[]>([]);
  const [recentPurchase, setRecentPurchase] = useState<string>('');
  const [tasks, setTasks] = useState(0);
  const [updates, setUpdates] = useState<any[]>([]);

  // Fetch user, rewards, downloads, etc.
  useEffect(() => {
    if (!user?.id) return;
    (async () => {
      // Fetch user first name
      const { data: userData } = await supabase.functions.invoke('get-wc-data', {
        body: { method: 'GET', endpoint: `cwc/customer/${user.id}`, user_id: user.id }
      });
      if (userData?.Success && userData.Data?.first_name) setFirstName(userData.Data.first_name);

      // Fetch rewards (example - replace with real API)
      setRewards(2500); // replace with API result

      // Fetch downloads
      const { data: dlData } = await supabase.functions.invoke('get-wc-data', {
        body: { method: 'GET', endpoint: 'cwc/downloads', user_id: user.id }
      });
      if (dlData?.Success && Array.isArray(dlData.Data)) {
        setDownloads(dlData.Data.slice(0, 3)); // recent 3
        if (dlData.Data.length) setRecentPurchase(dlData.Data[0].product_name);
      }

      // Tasks and updates (placeholder)
      setTasks(2); // replace with API
      setUpdates([
        { icon: <IconCheckCircle />, title: "New download available", subtitle: "You can now download 'DP-203 Exam Guide'", unread: true },
        { icon: <IconEnvelope />, title: "Account update", subtitle: "Your email address was changed" }
      ]);
    })();
  }, [user]);

  return (
    <Page>
<Welcome>Welcome back, {firstName || "there"}!</Welcome>
      <DashboardLayout>
        {/* Main column */}
        <MainCol>
          <Card>
            <SectionTitle>Quick Stats</SectionTitle>
            <StatRow>
              <StatCard>
                <StatLabel><IconMedal />My Rewards</StatLabel>
                <StatValue>{rewards.toLocaleString()} Pts</StatValue>
              </StatCard>
              <StatCard>
                <StatLabel><IconBag />Recent Purchase</StatLabel>
                <StatValue>{recentPurchase || '-'}</StatValue>
              </StatCard>
              <StatCard>
                <StatLabel><IconCheckCircle />Tasks</StatLabel>
                <StatValue>{tasks}</StatValue>
              </StatCard>
            </StatRow>
            <ActionsRow>
              <ActionBtn to="/downloads">Go to Downloads</ActionBtn>
              <ActionBtn to="/orders">Order History</ActionBtn>
              <ActionBtn to="/settings">Account Settings</ActionBtn>
            </ActionsRow>
          </Card>

          <Card>
            <SectionTitle>Recent Downloads</SectionTitle>
            <RecentDownloads>
              {downloads.length === 0 && <div style={{ color: "#999" }}>No downloads yet.</div>}
              {downloads.map(d => (
                <DownloadRow key={d.download_id}>
                  <DownloadImg src={d.product_image} alt={d.product_name} />
                  <DownloadInfo>
                    <div className="name">{d.product_name}</div>
                    <div className="meta">Expires: {d.access_expires || "-"} &nbsp; | &nbsp; Left: {d.downloads_remaining ?? "-"}</div>
                  </DownloadInfo>
                  <DownloadActions>
                    <ActionBtn to={`/downloads`}>Download</ActionBtn>
                    {d.supabase_file_id && (
                      <ActionBtn to={`/practice/${d.supabase_file_id}`}>Practice</ActionBtn>
                    )}
                  </DownloadActions>
                </DownloadRow>
              ))}
            </RecentDownloads>
          </Card>
        </MainCol>

        {/* Side column */}
        <SideCol>
          <UpdatesCard>
            <SectionTitle>Updates & Notifications</SectionTitle>
            {updates.map((u, i) => (
              <UpdateItem key={i}>
                {u.icon}
                <div className="text">
                  <div className="title">{u.title}</div>
                  <div className="subtitle">{u.subtitle}</div>
                </div>
                {u.unread && <div style={{
                  width: 10, height: 10, borderRadius: '50%', background: "#7367f0", alignSelf: 'center'
                }} />}
              </UpdateItem>
            ))}
          </UpdatesCard>
        </SideCol>
      </DashboardLayout>
    </Page>
  );
}
