// src/pages/DashboardPage.tsx
import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { useUser } from '../context/UserContext';
import { supabase } from '../supabaseClient';

// --- SVG Icons (no changes to your business logic) ---
const IconCheckCircle = ({ color = 'currentColor' }) => <svg width="24" height="24" viewBox="0 0 24 24" fill={color}><path d="M12 2C6.48 2 2 6.48 2 12s4.48 10 10 10 10-4.48 10-10S17.52 2 12 2zm-2 15l-5-5 1.41-1.41L10 14.17l7.59-7.59L19 8l-9 9z" /></svg>;
const IconEnvelope = ({ color = 'currentColor' }) => <svg width="24" height="24" viewBox="0 0 24 24" fill={color}><path d="M20 4H4c-1.1 0-1.99.9-1.99 2L2 18c0 1.1.9 2 2 2h16c1.1 0 2-.9 2-2V6c0-1.1-.9-2-2-2zm0 4l-8 5-8-5V6l8 5 8-5v2z" /></svg>;
const IconFlag = ({ color = 'currentColor' }) => <svg width="24" height="24" viewBox="0 0 24 24" fill={color}><path d="M14.4 6L14 4H5v17h2v-7h5.6l.4 2h7V6h-5.6z" /></svg>;

// --- Styled Components ---
const WelcomeHeader = styled.h2`
  font-size: 28px;
  font-weight: 600;
  color: ${({ theme }) => theme.colors.textPrimary};
  margin-bottom: 24px;
`;

const DashboardLayout = styled.div`
  display: grid;
  grid-template-columns: 2fr 1fr;
  gap: 24px;
  align-items: flex-start;
  @media (max-width: 950px) {
    grid-template-columns: 1fr;
  }
`;

const MainColumn = styled.div`
  display: flex;
  flex-direction: column;
  gap: 24px;
`;

// --- Recent Orders Section ---
const OrdersSection = styled.div`
  background-color: #fff;
  border-radius: ${({ theme }) => theme.borderRadius.large};
  box-shadow: ${({ theme }) => theme.shadows.card};
  padding: 26px 20px 16px 20px;
`;

const OrdersHeader = styled.div`
  font-size: 21px;
  font-weight: 600;
  margin-bottom: 14px;
  color: ${({ theme }) => theme.colors.textPrimary};
`;

const OrderTable = styled.table`
  width: 100%;
  border-collapse: separate;
  border-spacing: 0 12px;
  font-size: 15px;
  th {
    text-align: left;
    color: #6b6b8d;
    font-weight: 600;
    font-size: 14px;
    padding-bottom: 5px;
  }
  td {
    background: #f8f8fa;
    padding: 13px 14px;
    border-radius: 8px;
    color: #282856;
    font-size: 15px;
    font-weight: 500;
    vertical-align: middle;
  }
  tr {
    box-shadow: 0 2px 10px #ecebfa33;
  }
`;

const ViewButton = styled.button`
  background: ${({ theme }) => theme.colors.primary};
  color: #fff;
  border: none;
  padding: 7px 18px;
  border-radius: 7px;
  font-size: 15px;
  font-weight: 500;
  cursor: pointer;
  transition: background 0.18s;
  &:hover {
    background: #333387;
  }
`;

// --- Updates / Notifications (unchanged) ---
const RightColumn = styled.div``;

const UpdatesCard = styled.div`
  padding: 24px;
  background-color: ${({ theme }) => theme.colors.surface};
  border-radius: ${({ theme }) => theme.borderRadius.large};
  border: 1px solid ${({ theme }) => theme.colors.lightGrey};
  box-shadow: ${({ theme }) => theme.shadows.card};
  display: flex;
  flex-direction: column;
  min-height: 140px;
`;

const UpdatesHeader = styled.h3`
  font-size: 18px;
  font-weight: 600;
  color: ${({ theme }) => theme.colors.textPrimary};
  margin-bottom: 8px;
`;

const UpdateItem = styled.div<{ $isUnread?: boolean }>`
  display: flex;
  align-items: center;
  gap: 16px;
  padding: 16px 0;
  border-bottom: 1px solid ${({ theme }) => theme.colors.lightGrey};
  &:last-child {
    border-bottom: none;
  }
  .icon {
    font-size: 24px;
  }
  .title {
    font-weight: ${({ $isUnread }) => ($isUnread ? '600' : '500')};
    color: ${({ theme }) => theme.colors.textPrimary};
    font-size: 15px;
  }
  .subtitle {
    color: ${({ theme }) => theme.colors.textSecondary};
    font-size: 13px;
    margin-top: 4px;
  }
  .unread-dot {
    height: 8px;
    width: 8px;
    background-color: ${({ theme }) => theme.colors.primary};
    border-radius: 50%;
    margin-left: auto;
  }
`;

// --- MAIN DASHBOARD ---
const DashboardPage = () => {
  // --- Business Logic (Untouched) ---
  const { user } = useUser();
  const [displayName, setDisplayName] = useState('there');
  const [orders, setOrders] = useState<any[]>([]);

  useEffect(() => {
    if (!user) return;
    // Get first name (untouched)
    const fetchUserName = async () => {
      try {
        const { data: apiResponse, error } = await supabase.functions.invoke('get-wc-data', {
          body: { 
            method: 'GET', 
            endpoint: `cwc/customer/${user.id}`, 
            user_id: user.id 
          },
        });
        if (error) throw error;
        if (apiResponse && apiResponse.Success && apiResponse.Data.first_name) {
          setDisplayName(apiResponse.Data.first_name);
        }
      } catch (err) {
        console.error("Error fetching user name for dashboard:", err);
      }
    };

    // --- Fetch Recent Orders ---
    const fetchOrders = async () => {
      try {
