            endpoint: `cwc/orders`,
            user_id: user.id,
            customer: user.id, // If your API expects this
          },
        });
        if (error) throw error;
        if (apiResponse && apiResponse.Success && Array.isArray(apiResponse.Data)) {
          setOrders(apiResponse.Data.slice(0, 5)); // Show top 5 recent orders
        }
      } catch (err) {
        console.error("Error fetching recent orders for dashboard:", err);
      }
    };

    fetchUserName();
    fetchOrders();
  }, [user]);

