// src/components/common/Sidebar.tsx
import React from 'react';
import styled from 'styled-components';
import { NavLink } from 'react-router-dom';
import { PiHouse, PiShoppingBag, PiDownloadSimple, PiBookOpen, PiMapPin, PiUser, PiSignOut } from 'react-icons/pi';
import logo from '../../assets/images/logo.png';

const SidebarContainer = styled.div`
  width: 260px;
  background-color: ${({ theme }) => theme.colors.surface};
  height: 100vh;
  display: flex;
  flex-direction: column;
  border-right: 1px solid ${({ theme }) => theme.colors.lightGrey};
  position: fixed;
  left: 0;
  top: 0;
`;

const LogoContainer = styled.div`
  padding: 24px;
  display: flex;
  justify-content: center;
  align-items: center;
  margin-bottom: 20px;

  img {
    width: 150px;
  }
`;

const NavList = styled.nav`
  display: flex;
  flex-direction: column;
`;

const StyledNavLink = styled(NavLink)`
  display: flex;
  align-items: center;
  padding: 16px 24px;
  color: ${({ theme }) => theme.colors.textSecondary};
  text-decoration: none;
  font-weight: 500;
  transition: all 0.2s ease-in-out;

  svg {
    margin-right: 16px;
    font-size: 22px;
  }

  &.active, &:hover {
    background-color: #E6F0FF; 
    color: ${({ theme }) => theme.colors.primary};

    svg {
      color: ${({ theme }) => theme.colors.primary};
    }
  }
`;

const LogoutButton = styled.button`
  /* Making the button look just like a NavLink */
  display: flex;
  align-items: center;
  width: 100%;
  padding: 16px 24px;
  color: ${({ theme }) => theme.colors.textSecondary};
  font-size: 16px;
  font-weight: 500;
  font-family: inherit;
  background: none;
  border: none;
  cursor: pointer;
  text-align: left;

  svg {
    margin-right: 16px;
    font-size: 22px;
  }

  &:hover {
    background-color: #E6F0FF; 
    color: ${({ theme }) => theme.colors.primary};
    svg {
      color: ${({ theme }) => theme.colors.primary};
    }
  }
`;


const Sidebar = () => {
  return (
    <SidebarContainer>
      <LogoContainer>
        <img src={logo} alt="CertEmpire Logo" />
      </LogoContainer>
      <NavList>
        <StyledNavLink to="/" end> <PiHouse /> Dashboard </StyledNavLink>
        <StyledNavLink to="/orders"> <PiShoppingBag /> Orders </StyledNavLink>
        <StyledNavLink to="/downloads"> <PiDownloadSimple /> Downloads </StyledNavLink>
        <StyledNavLink to="/practice"> <PiBookOpen /> Practice </StyledNavLink>
        <StyledNavLink to="/addresses"> <PiMapPin /> Addresses </StyledNavLink>
        <StyledNavLink to="/account-details"> <PiUser /> Account Details </StyledNavLink>
        <LogoutButton onClick={() => alert('Logout clicked!')}> <PiSignOut /> Log out </LogoutButton>
      </NavList>
    </SidebarContainer>
  );
};

export default Sidebar;
